            union() {
                // Main blade body
                linear_extrude(height=blade_thickness, center=true)
                    polygon(points=[
                        [0, -blade_width/2],
                        [blade_length, -blade_width/3],
                        [blade_length, blade_width/3],
                        [0, blade_width/2]
                    ]);

                // Blade spine reinforcement
                translate([0, 0, blade_thickness/2 + 0.5])
                    linear_extrude(height=1)
                        polygon(points=[
                            [0, -blade_width/2 + 2],
                            [blade_length - 10, -blade_width/3 + 1],
                            [blade_length - 10, blade_width/3 - 1],
                            [0, blade_width/2 - 2]
                        ]);
            }

            // Cutting edge bevel
            translate([blade_length - 8, 0, -blade_thickness/2 - 0.5])
                cube([8, blade_width, blade_thickness/2 + 1], center=true);

            // Nail puller notch
