// Connection bracket
bracket_length = 10;
bracket_width = 10;
bracket_height = 6;

// Handle with grip
module handle() {
    color(handle_color) {
        // Main handle shaft
        cylinder(h=handle_length, d=handle_diameter, $fn=32);

        // Grip section
        translate([0, 0, handle_length - grip_length]) 
            color(grip_color) 
            cylinder(h=grip_length, d=grip_diameter, $fn=32);

        // Grip rings for texture
        for(i = [0:3]) {
            translate([0, 0, handle_length - grip_length + i*3 + 1]) 
                color(grip_color) 
                cylinder(h=1, d=grip_diameter+1, $fn=32);
        }
    }
}

// Rake head with tines
module rake_head() {
    // Main head body
    color(metal_color) {
        // Head plate
        translate([-head_width/2, -head_thickness/2, -head_height])
            cube([head_width, head_thickness, head_height]);

        // Tapered connection to bracket
        translate([-bracket_width/2, -head_thickness/2, -head_height-bracket_height])
            cube([bracket_width, head_thickness, bracket_height]);
    }
}

// Individual tine
module tine() {
    color(metal_color) {
        // Tine shaft
        translate([-tine_width/2, -tine_thickness/2, -tine_length])
            cube([tine_width, tine_thickness, tine_length]);

        // Tine tip - beveled
        translate([-tine_width/2, -tine_thickness/2, -tine_length])
            rotate([0, 45, 0])
            cube([tine_width, tine_thickness*sqrt(2), tine_thickness]);
    }
}

// Complete rake assembly
module rake() {
    // Handle
    rotate([90, 0, 0])
    handle();

    // Connection bracket
    translate([0, 0, -bracket_height])
    color(metal_color) {
        // Main bracket
        rotate([90, 0, 0])
        cylinder(h=bracket_length, d=bracket_width, $fn=32);

        // Reinforcement ring
        translate([0, 0, -bracket_length/2])
        rotate([90, 0, 0])
        difference() {
            cylinder(h=bracket_length/2, d=bracket_width+2, $fn=32);
            cylinder(h=bracket_length/2+1, d=bracket_width-1, $fn=32);
        }
    }

    // Rake head
    translate([0, bracket_length, 0])
    rake_head();

    // Tines
    translate([0, bracket_length, -head_height])
    for(i = [0:tine_count-1]) {
        x_pos = -head_width/2 + head_width/(tine_count-1)*i;
        translate([x_pos, 0, 0])
        tine();
    }
}

