// Realistic Medium-Complexity 3D-Printable Scoop Tool
// Overall length ~85 mm, scoop width ~45 mm, handle ~15 mm thick

// Parameters
handle_len = 60;
handle_d = 15;
scoop_w = 45;
scoop_d = 40;
scoop_h = 20;
wall_t = 2.4;      // 2-3 perimeters common for FDM
fillet_r = 3;

// Colors
color_handle = "SaddleBrown";
color_scoop  = "Silver";

// Utility module for rounded edges
module round_box(size, r, center=false) {
    translate(center ? -size/2 : [0,0,0])
    hull() {
        for(dx=[0,size.x], dy=[0,size.y], dz=[0,size.z])
            translate([dx,dy,dz]-[r,r,r])
                sphere(r, $fn=16);
    }
}

// Handle grip rings
module grip_rings(count=5) {
    for(i=[0:count-1]) {
        translate([0,0,i*handle_len/count])
            difference() {
                cylinder(h=3, d=handle_d+2, center=true, $fn=64);
                cylinder(h=4, d=handle_d-1, center=true, $fn=64);
            }
    }
}

// Main handle
module handle() {
    color(color_handle)
    difference() {
        union() {
            // Primary shaft
            cylinder(h=handle_len, d=handle_d, center=false, $fn=64);
            grip_rings();
            // Ferrule ring at joint
            translate([0,0,handle_len-2])
                cylinder(h=4, d=handle_d+3, $fn=64);
        }
        // Hollow shaft to save material
        translate([0,0,-1])
            cylinder(h=handle_len+2, d=handle_d-wall_t*2, $fn=64);
    }
}

// Scoop bowl with realistic taper and pour spout
module scoop() {
    color(color_scoop)
    difference() {
        union() {
            // Outer bowl shell
            scale([1, 0.9, 0.7])
                sphere(d=scoop_w, $fn=64);
            translate([0,0,-scoop_h/2])
                cube([scoop_w, scoop_d, scoop_h], center=true);
            // Reinforcement ridge
            translate([0,0,-scoop_h/2+wall_t])
                round_box([scoop_w+4, 4, wall_t*2], fillet_r, center=true);
        }
        // Inner cavity
        translate([0,0,-scoop_h/2+wall_t])
            scale([1, 0.85, 0.65])
                sphere(d=scoop_w-wall_t*2, $fn=64);
        translate([0,0,-scoop_h/2+wall_t])
            cube([scoop_w-wall_t*2, scoop_d-wall_t*2, scoop_h], center=true);
    }
    // Pour spout
    translate([0, scoop_d/2, 0])
        rotate([0,30,0])
            scale([0.3,1.2,0.3])
                cylinder(h=scoop_w/2, d=scoop_w/4, $fn=16);
}

// Assembly
handle();
translate([0,0,handle_len-2])
    rotate([90,0,0])
        scoop();

// Realistic Fork Tool - Medium Complexity
// 3D printable design with proper wall thickness and no overhangs

// Color definitions
fork_color = "Silver";
handle_color = "#2F2F2F";

// Main parameters
fork_length = 90;
handle_length = 45;
handle_diameter = 8;
tine_length = 45;
tine_width = 2.5;
tine_spacing = 5;
num_tines = 4;
wall_thickness = 1.5;

module fork_tine(length, width) {
    // Single tine with rounded tip
    difference() {
        union() {
            // Main tine body
            translate([0, 0, length/2])
                cube([width, width, length], center=true);

            // Rounded tip
            translate([0, 0, length])
                sphere(d=width, $fn=16);
        }
        // Hollow out for 3D printing
        translate([0, 0, length/2])
            cube([width-wall_thickness*2, width-wall_thickness*2, length-wall_thickness], center=true);
    }
}

module fork_head() {
    // Fork head with tines
    color(fork_color) {
        // Base plate
        translate([0, 0, wall_thickness/2])
            cube([(num_tines-1)*tine_spacing + tine_width, tine_width*2, wall_thickness], center=true);

        // Tines
        for(i = [0:num_tines-1]) {
            translate([(i-(num_tines-1)/2)*tine_spacing, 0, wall_thickness])
                fork_tine(tine_length, tine_width);
        }
    }
}

module handle_grip() {
    // Ergonomic handle with grip pattern
    color(handle_color) {
        difference() {
            // Main handle body
            cylinder(h=handle_length, d=handle_diameter, $fn=32);

            // Hollow handle for material saving
            cylinder(h=handle_length, d=handle_diameter-wall_thickness*2, $fn=32);
        }

        // Grip rings
        for(i = [1:6]) {
            translate([0, 0, i*handle_length/7])
                difference() {
                    cylinder(h=2, d=handle_diameter+1, $fn=32);
                    cylinder(h=2.5, d=handle_diameter-1, $fn=32);
                }
        }
    }
}

module fork_neck() {
    // Transition between handle and fork head
    color(fork_color) {
        hull() {
            // Handle connection
            translate([0, 0, handle_length/2])
                cylinder(h=handle_length/3, d=handle_diameter*0.8, $fn=32);

            // Fork head connection
            translate([0, 0, handle_length + 5])
                cylinder(h=10, d=tine_width*4, $fn=32);
        }
    }
}

// Assembly
translate([0, 0, handle_length/2])
    handle_grip();

translate([0, 0, handle_length + 5])
    fork_neck();

translate([0, 0, handle_length + 15])
    fork_head();

// Harrow tool - realistic style, medium complexity
// Overall dimensions: ~80mm wide x 70mm deep x 40mm tall

// Define colors
frame_color = "Silver";
