// Main frame structure
module frame() {
    color(frame_color) {
        // Central cross beam
        translate([0, 0, 15]) cube([70, 8, 6], center=true);

        // Side rails
        translate([-30, 0, 15]) cube([4, 60, 6], center=true);
        translate([30, 0, 15]) cube([4, 60, 6], center=true);

        // Front support
        translate([0, 25, 15]) cube([74, 4, 6], center=true);

        // Rear support
        translate([0, -25, 15]) cube([74, 4, 6], center=true);
    }
}

// Individual harrow blade
module blade() {
    color(blade_color) difference() {
        // Main blade body
        rotate([90, 0, 0]) cylinder(h=12, d1=8, d2=6, center=false);

        // Taper the bottom
        translate([0, 0, -0.5]) rotate([90, 0, 0]) cylinder(h=13, d1=7, d2=3, center=false);

        // Create cutting edge
        translate([0, 6, 0]) rotate([0, 90, 0]) cube([12, 2, 8], center=true);
    }
}

// Blade assembly with mounting bracket
module blade_assembly(x_pos, y_pos, angle) {
    translate([x_pos, y_pos, 18]) rotate([0, 0, angle]) {
        // Mounting bracket
        color(frame_color) cube([10, 4, 8], center=true);

        // Blade
        translate([0, 0, 4]) blade();
    }
}

// Handle assembly
module handle() {
    color(handle_color) {
        // Main handle tube
        translate([0, 30, 35]) rotate([90, 0, 0]) cylinder(h=40, d=8, center=false);

        // Handle grip
        translate([0, 30, 55]) rotate([90, 0, 0]) cylinder(h=15, d=12, center=false);

        // Connection to frame
        translate([0, 25, 22]) rotate([45, 0, 0]) cylinder(h=20, d=6, center=false);
    }
}

// Generate complete harrow
union() {
    // Main frame
    frame();

    // Handle
    handle();

    // Blade rows - staggered pattern
    // Front row
    for (i = [-3:3]) {
        blade_assembly(i * 10, 20, -15);
    }

    // Second row
    for (i = [-2.5:3]) {
        blade_assembly(i * 10 - 5, 10, 0);
    }

    // Third row
    for (i = [-3:3]) {
        blade_assembly(i * 10, 0, 15);
    }

    // Fourth row
    for (i = [-2.5:3]) {
        blade_assembly(i * 10 - 5, -10, 0);
    }

    // Rear row
    for (i = [-3:3]) {
        blade_assembly(i * 10, -20, -15);
    }
}

