// Curved sickle blade with sharpened edge
module blade() {
    color(blade_color)
    difference() {
        // Main blade body - curved shape
        translate([0, 0, 2])
        rotate([90, 0, 0])
        linear_extrude(3)
        offset(1)
        polygon(points=[
            [0, 0], [35, 0], [65, 15], [70, 25], [60, 35], [40, 30], [15, 20], [0, 0]
        ]);

        // Create cutting edge bevel
        translate([0, 0, 2.5])
        rotate([90, 0, 0])
        linear_extrude(2)
        offset(0.5)
        polygon(points=[
            [0, 0], [35, 0], [65, 15], [70, 25], [60, 35], [40, 30], [15, 20], [0, 0]
        ]);

        // Blade spine for strength
        translate([5, 0, 3])
        rotate([90, 0, 0])
        linear_extrude(1)
        polygon(points=[
            [0, 5], [30, 3], [50, 8], [55, 12], [45, 15], [25, 12], [0, 5]
        ]);
    }

    // Blade tang (extension into handle)
    color(blade_color)
    translate([-25, 0, 2])
    cube([25, 8, 3]);
}

// Wooden handle with grip texture
module handle() {
    color(handle_color)
    difference() {
        // Main handle - tapered cylinder
        translate([-70, 0, 2])
        rotate([0, 90, 0])
        cylinder(h=45, r1=4, r2=3.5, $fn=16);

        // Grip grooves for better handling
        for(i = [1:4]) {
            translate([-65 + i*8, 0, 2])
            rotate([0, 90, 0])
            difference() {
                cylinder(h=6, r=4.2, $fn=16);
                translate([0, 0, -1])
                cylinder(h=8, r=3.8, $fn=16);
            }
        }
    }

    // Handle end cap
    color(handle_color)
    translate([-70, 0, 2])
    rotate([0, 90, 0])
    cylinder(h=3, r=4.5, $fn=16);
}

// Metal ferrule connecting blade to handle
module ferrule() {
    color(rivet_color)
    difference() {
        // Main ferrule tube
        translate([-25, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=15, r=5, $fn=32);

        // Central hole for tang
        translate([-25, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=16, r=2.5, $fn=32);
    }

    // Rivet details
    for(i = [-15, -10, -5]) {
        color(rivet_color)
        translate([i, 0, 0])
        rotate([90, 0, 0])
        cylinder(h=2, r=1.5, $fn=8);
