module scythe_blade() {
    union() {
        // Main curved blade body
        translate([0,0,0]) rotate([0,0,15]) {
            difference() {
                // Outer curved shape
                hull() {
                    translate([0,0,0]) sphere(d=16);
                    translate([-35,-5,0]) sphere(d=14);
                    translate([-65,-15,0]) sphere(d=10);
                    translate([-85,-35,0]) sphere(d=6);
                }
                // Inner cutout
                hull() {
                    translate([-10,-2,-blade_thickness]) sphere(d=12);
                    translate([-55,-20,-blade_thickness]) sphere(d=8);
                    translate([-80,-40,-blade_thickness]) sphere(d=4);
                }
                scale([1,1,0.1]) translate([0,0,-blade_thickness*5]) cube(200,center=true);
            }
        }

        // Cutting edge bevel
        color(edge_col) translate([0,0,-blade_thickness/2-0.1]) rotate([0,0,15]) {
            hull() {
                translate([0,0,0]) sphere(d=15.8);
                translate([-85,-35,0]) sphere(d=5.8);
            }
        }

        // Reinforcement rib along spine
        translate([-45,5,-1]) rotate([0,0,15]) {
            hull() {
                for(i=[0:4]) {
                    translate([-i*11, i*3, 0]) sphere(d=3.5-i*0.4);
                }
            }
        }
    }
}

module handle_grip() {
    difference() {
        cylinder(h=22, d=grip_d, $fn=32);
        for(i=[0:3]) {
            rotate([0,0,i*90]) translate([grip_d/2-1,0,-1]) cube([2, 0.5, 24]);
        }
    }
}

module handle() {
    color(wood_col) {
        // Main snath
        union() {
            translate([0,0,0]) rotate([0,90,0]) cylinder(h=70, d=handle_d, $fn=24);
            translate([0,70,0]) rotate([20,90,0]) cylinder(h=45, d=handle_d*0.9, $fn=24);
        }

        // Upper grip
        translate([0,45,0]) rotate([90,0,0]) handle_grip();

        // Lower grip
        translate([0,15,0]) rotate([0,0,-20]) rotate([90,0,0]) {
            scale([1,0.8,1]) handle_grip();
        }
    }
}

module tang() {
    color(metal_col) {
        union() {
            // Tang extension
            translate([-20,0,-1]) cube([20, 7, 2]);

            // Rivet heads
            for(i=[0:1]) {
                translate([-15-i*8, 0, 0]) sphere(d=2.5);
            }
        }
    }
}

