    difference(){
        translate([0,handle_len/2,0])
            rotate([90,0,0])
                cylinder(h=5,d=handle_d+4,$fn=32);
        translate([0,handle_len/2,0])
            rotate([90,0,0])
                cylinder(h=6,d=handle_d-2,$fn=32);
