// Handle grip texture module
module grip_texture() {
    for (i = [0:4]) {
        translate([0, 0, -handle_diameter/2 + i*handle_diameter/5])
        difference() {
            cylinder(h=handle_diameter/10, d=handle_diameter*1.1);
            cylinder(h=handle_diameter/10+1, d=handle_diameter);
        }
    }
