// Handle module
module handle(is_left = true) {
    color(handle_color) {
        // Main handle body
        rotate([0, 90, 0])
        difference() {
            cylinder(h=handle_length, d=handle_diameter);
            // Ergonomic curve
            translate([handle_length/2, 0, 0])
            rotate([0, 0, 90])
            cube([handle_length, handle_diameter*2, handle_diameter*2], center=true);
        }

        // Grip area
        color(grip_color) {
            translate([is_left ? -handle_length/2 : handle_length/2, 0, 0])
            rotate([0, 90, 0])
            grip_texture();
        }

        // Finger rest
        translate([is_left ? -handle_length/2 - 5 : handle_length/2 + 5, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=8, d=handle_diameter*1.2);
