        // Main blade
        translate([0, 0, is_top ? blade_thickness/2 : -blade_thickness/2])
        difference() {
            linear_extrude(height=blade_thickness)
            polygon(points=[
                [0, 0],
                [blade_length, 0],
                [blade_length-10, 5],
                [blade_length-20, 8],
                [5, 8],
                [0, 5]
            ]);

            // Cutting edge bevel
            translate([blade_length-15, 2, 0])
            cube([15, 6, blade_thickness*2]);
        }

        // Blade spine
        translate([0, 0, is_top ? blade_thickness : -blade_thickness])
        cube([blade_length, 2, blade_thickness/2]);
    }
