// Main thimble body
module thimble_body() {
    difference() {
        // Outer shell with reinforced rim
        union() {
            // Main cylindrical body
            cylinder(h=thimble_height, r=thimble_radius, $fn=100);

            // Reinforced rim at top
            translate([0, 0, thimble_height - rim_height])
                cylinder(h=rim_height, r1=thimble_radius, r2=thimble_radius + 2, $fn=100);
        }

        // Inner cavity - tapered for finger comfort
        translate([0, 0, -1])
            cylinder(h=thimble_height + 2, r1=thimble_radius - wall_thickness - 3, 
                    r2=thimble_radius - wall_thickness, $fn=100);
