    color("silver")
    for (row = [0 : dimple_rows - 1]) {
        row_height = 8 + row * 4.5;
        row_radius = thimble_radius - dimple_depth - 0.5;
        count = dimples_per_row[row];

        for (i = [0 : count - 1]) {
            angle = 360 * i / count;
            // Calculate position on curved surface
            x = row_radius * cos(angle);
            y = row_radius * sin(angle);

            translate([x, y, row_height])
                rotate([90, 0, angle])
                    sphere(dimple_radius * 2, $fn=20);
        }
    }
}

