            color("silver") cube([80, 12, 6], center=true);

            // Reinforcement ridges
            translate([0, 0, 3.5])
            color("silver") cube([80, 10, 1], center=true);
        }

        // Measurement scale markings
        for(i=[0:39]) {
            translate([-40 + i*2, 6.1, 2.5])
            color("black") cube([0.2, 0.5, 2]);
        }
    }
}

module fixed_jaw() {
    // Fixed measuring jaw
    translate([-40, 0, 0]) {
        difference() {
            union() {
                // Jaw base
                color("silver") cube([10, 10, 8], center=true);

                // Measuring surface
                translate([5, 0, 0])
                color("silver") cube([2, 8, 6], center=true);

                // Inside measuring blade
                translate([2, 0, -5])
                color("silver") cube([6, 3, 4], center=true);
            }

            // Depth rod slot
            translate([0, 0, -5])
            cube([6, 3, 10], center=true);
        }
    }
}

module sliding_jaw() {
    // Sliding jaw assembly
    translate([20, 0, 0]) {
        difference() {
            union() {
                // Slider body
                color("silver") cube([15, 14, 10], center=true);

                // Thumb wheel
                translate([0, 8, 3])
                rotate([90, 0, 0])
                color("darkgray") cylinder(h=4, r=5, center=true);

                // Measuring surface
                translate([-7.5, 0, 0])
                color("silver") cube([2, 8, 6], center=true);

                // Inside measuring blade
                translate([-2, 0, -5])
                color("silver") cube([6, 3, 4], center=true);
            }

            // Beam slot
            translate([0, 0, -0.5])
            cube([16, 12.5, 7], center=true);

            // Thumb wheel grooves
            for(i=[0:19]) {
                rotate([0, 0, i*18])
                translate([0, 8, 3])
                rotate([90, 0, 0])
                translate([0, 5, 0])
                color("black") cube([0.3, 0.5, 4], center=true);
            }
        }
    }
}

module depth_rod() {
