handle_length = 30;

// Colors
beam_color = "Silver";
handle_color = "Black";
pivot_color = "Gold";
blade_color = "Gray";

// Main beam with measurement markings
module trammel_beam() {
    color(beam_color) {
        // Main beam body
        difference() {
            cube([beam_length, beam_width, beam_height], center = true);

            // Hollow out center for weight reduction
            translate([0, 0, 0])
                cube([beam_length - beam_thickness*2, beam_width - beam_thickness*2, beam_height - beam_thickness], center = true);
        }

        // Measurement markings (mm scale)
        for(i = [0:5:beam_length-10]) {
            translate([-beam_length/2 + 5 + i, beam_width/2 + 0.1, 0])
                cube([0.5, 2, 0.5]);
        }
    }
}

// Adjustable pivot block with thumb screw
module pivot_block() {
    color(pivot_color) {
        translate([0, 0, 0]) {
            // Main block
            cube([12, beam_width + 6, beam_height + 4], center = true);

            // Thumb screw
            translate([0, 0, beam_height/2 + 6])
                cylinder(h = 8, r = 5, $fn = 20);

            // Screw head knurling
            for(i = [0:30:359]) {
                rotate([0, 0, i])
                    translate([4.5, 0, beam_height/2 + 6 + 4])
                        cube([1, 0.5, 2]);
            }

            // Beam channel
            translate([0, 0, 0])
                cube([beam_width + 2, beam_width + 2, beam_height + 6], center = true);

            // Pivot point
            translate([0, 0, -beam_height/2 - 3])
                cylinder(h = 6, r1 = 3, r2 = 2, center = true);
        }
    }
}

// Handle grip with finger grooves
module trammel_handle() {
    color(handle_color) {
        translate([0, 0, 0]) {
            // Main handle body
            cylinder(h = handle_length, r = handle_diameter/2, center = true);

            // Finger grooves
            for(i = [0:8:handle_length-8]) {
                translate([0, 0, -handle_length/2 + 4 + i])
                    rotate_extrude(angle = 360, $fn = 20)
                        translate([handle_diameter/2 - 1, 0, 0])
                            circle(r = 1);
            }

            // Center hole for pencil/pen
            translate([0, 0, 0])
                cylinder(h = handle_length + 2, r = 3, center = true);

            // Grip texture rings
            for(i = [0:5:handle_length-5]) {
                translate([0, 0, -handle_length/2 + 2.5 + i])
                    cylinder(h = 0.5, r = handle_diameter/2 + 0.5, center = true);
            }
        }
    }
}

// Pencil holder assembly
module pencil_holder() {
    color(pivot_color) {
        translate([beam_length/2 - 15, 0, 0]) {
            // Holder block
            cube([10, beam_width + 4, beam_height + 6], center = true);

            // Clamp mechanism
            translate([5, 0, 0])
                rotate([0, 90, 0])
                    cylinder(h = 10, r = 4, center = true);

            // Thumb wheel
            translate([8, 0, 0])
                rotate([0, 90, 0])
                    cylinder(h = 3, r1 = 6, r2 = 4);

            // Pencil channel
            translate([0, 0, 0])
                rotate([90, 0, 0])
                    cylinder(h = beam_width + 4, r = 3, center = true);
        }
    }
}

// Blade holder for needle point
module blade_holder() {
    color(blade_color) {
        translate([-beam_length/2 + 15, 0, 0]) {
            // Main holder
            cube([10, beam_width + 4, beam_height + 6], center = true);

            // Needle point
            translate([0, 0, -beam_height/2 - 8])
                cylinder(h = 12, r1 = 1, r2 = 0.2);

            // Protective collar
            translate([0, 0, -beam_height/2 - 4])
                cylinder(h = 4, r = 3);
        }
    }
}

// Assembly
module trammel_assembly() {
    // Main beam
    translate([0, 0, 0])
        trammel_beam();

    // Pivot block (center)
    translate([0, 0, 0])
        pivot_block();

    // Handles at both ends
    translate([beam_length/2 - 5, 0, beam_height/2 + handle_length/2])
        rotate([90, 0, 0])
            trammel_handle();

    translate([-beam_length/2 + 5, 0, beam_height/2 + handle_length/2])
        rotate([90, 0, 0])
            trammel_handle();

    // Pencil holder
    pencil_holder();

    // Blade holder
    blade_holder();
}

