    union() {
        // Main blade body
        translate([0, 0, -blade_length/2-15])
            cube([blade_thickness, 8, blade_length], center=true);

        // Blade teeth
        for(i = [-blade_length/2+2:2:blade_length/2-2]) {
            translate([0, 4, i])
                cube([blade_thickness, 1, 1.5], center=true);
        }
    }
}

// Base plate with adjustment mechanism
module base_plate() {
    color(metal_color)
    difference() {
        // Main plate
        cube([base_plate_length, base_plate_width, base_plate_thickness], center=true);

        // Blade slot
        translate([0, 0, base_plate_thickness/2+1])
            cube([blade_thickness+1, 8, base_plate_thickness+2], center=true);

        // Bevel adjustment markings
        for(i = [-30:15:30]) {
            translate([base_plate_length/2-5, i, base_plate_thickness-0.5])
                cube([5, 0.5, 1], center=true);
        }
    }
}

// Power cord
module power_cord() {
    color([0, 0, 0])
    translate([-motor_housing_length/2-5, 0, motor_housing_height/2-5])
    rotate([0, 90, 0])
        cylinder(h=30, d=6, $fn=16);
}

// Trigger switch
module trigger() {
    color([0.9, 0.9, 0.9])
    translate([0, -handle_diameter/2-2, handle_length/2+12])
        cube([6, 4, 10], center=true);
}

// Assemble the jigsaw
union() {
    // Handle
    rotate([90, 0, 0])
        translate([0, 0, -handle_length/2])
            handle();

    // Trigger
    rotate([90, 0, 0])
        trigger();

    // Motor housing
    translate([motor_housing_length/2-5, 0, handle_diameter/2-15])
        motor_housing();

    // Blade holder
    translate([motor_housing_length+5, 0, handle_diameter/2-15])
        blade_holder();

    // Blade
    translate([motor_housing_length+10, 0, handle_diameter/2-15])
        blade();

    // Base plate
    translate([motor_housing_length/2, 0, handle_diameter/2-15-motor_housing_height/2-base_plate_thickness/2-5])
        base_plate();

    // Power cord
    power_cord();
}

