module pipe_cutter() {
    difference() {
        union() {
            // Main body tube
            color(cutter_color) 
            cylinder(d = body_diameter, h = body_length, center = true);

            // Handle connection block
            translate([0, 0, -body_length/2 - 10])
            color(cutter_color)
            cube([body_diameter * 1.2, 15, 20], center = true);

            // Handle
            translate([0, -handle_length/2 - 10, -body_length/2 - 20])
            rotate([90, 0, 0])
            color(handle_color)
            cylinder(d = 12, h = handle_length, center = true);

            // Handle grip rings
            for(i = [-4:4]) {
                translate([0, -body_length/2 - 20 + i * 10, -body_length/2 - 20])
                rotate([90, 0, 0])
                color(grip_color)
                cylinder(d = 14, h = 3, center = true);
            }

            // Cutter wheel support arm
            translate([body_diameter/2 + 10, 0, -body_length/4])
            rotate([0, 90, 0])
            color(cutter_color)
            cube([20, 8, 8], center = true);

            // Cutter wheel
            translate([body_diameter/2 + 20, 0, -body_length/4])
            rotate([0, 90, 0])
            color(blade_color)
            cylinder(d = cutter_wheel_diameter, h = wheel_thickness, center = true);
        }

        // Central hole for pipe
        cylinder(d = 20, h = body_length + 2, center = true);

        // Screw hole
        translate([0, 0, body_length/2 - 5])
        cylinder(d = 6, h = 10, center = true);

        // Lightening holes in body
        for(i = [0:3]) {
            rotate([0, 0, i * 90])
            translate([body_diameter/2 - 5, 0, 0])
            cylinder(d = 8, h = body_length + 2, center = true);
        }

        // Wheel axle hole
        translate([body_diameter/2 + 20, 0, -body_length/4])
        rotate([0, 90, 0])
        cylinder(d = 3, h = wheel_thickness + 2, center = true);
    }

    // Adjustment knob
    translate([0, 0, body_length/2 + 5])
    difference() {
        color(cutter_color)
        cylinder(d = 15, h = 10, center = true);

        // Knurling pattern
        for(i = [0:23]) {
            rotate([0, 0, i * 15])
            translate([7, 0, -5])
            cube([0.5, 1, 10], center = true);
        }
    }

    // Knob handle
    translate([0, 0, body_length/2 + 15])
    rotate([90, 0, 0])
    color(handle_color)
    cylinder(d = 6, h = 15, center = true);
}

