// Color constants
handle_col = "darkslategray";
shank_col   = "silver";
tip_col     = "darkgoldenrod";

// Main dimensions
handle_len  = 45;
handle_dia  = 16;
shank_len   = 35;
shank_dia   = 6;
tip_len     = 10;
tip_size    = 3; // #2 Phillips

// Handle module with ergonomic grip
module handle() {
    color(handle_col)
    union() {
        // Main grip body
        cylinder(h = handle_len, d = handle_dia, $fn = 40);

        // Ergonomic finger grooves (6 around)
        for (i = [0:5]) {
            rotate([0, 0, i * 60])
                translate([handle_dia/2 - 1.5, 0, handle_len/2])
                    rotate([90, 0, 0])
                        cylinder(h = 12, d1 = 3, d2 = 5, $fn = 16);
        }

        // End cap with brand inset
        translate([0, 0, handle_len])
            cylinder(h = 4, d = handle_dia + 1, $fn = 40);

        // Brand inset
        difference() {
            translate([0, 0, handle_len + 2])
                cylinder(h = 2, d = 8, $fn = 30);
            translate([0, 0, handle_len + 1.9])
                cylinder(h = 2.2, d = 6, $fn = 30);
        }
    }
}

// Shank with anti-roll flats
module shank() {
    color(shank_col)
    union() {
        cylinder(h = shank_len, d = shank_dia, $fn = 30);

        // Two small flats to prevent rolling
        for (side = [0, 1]) {
            rotate([0, 0, side * 180])
                translate([shank_dia/2 - 0.5, 0, shank_len/2])
                    cube([1, shank_dia * 0.8, shank_len], center = true);
        }
    }
}

// Phillips #2 tip
module phillips_tip() {
    color(tip_col)
    intersection() {
        // Tapered cone
        cylinder(h1 = tip_len, d1 = tip_size, d2 = shank_dia, $fn = 16);

        // Four flutes creating cross pattern
        for (a = [0:90:270]) {
            rotate([0, 0, a])
                translate([0, 0, -1])
                    cube([tip_size * 1.8, 0.8, tip_len + 2], center = true);
        }
    }
}

// Assembly
translate([0, 0, 0]) handle();
translate([0, 0, handle_len]) shank();
translate([0, 0, handle_len + shank_len]) phillips_tip();

