        }

        // Blade insert
        color(color_blade) {
            translate([-jaw_width/2 + 2, -jaw_thickness/2 - 0.5, 22.5]) {
                cube([jaw_width - 4, 1, 5]);
            }
        }

        // Pivot point
        translate([0, 0, 15]) {
            cylinder(h = jaw_thickness + 4, d = 6, center = true);
        }

        // Spring mount
        translate([jaw_width/2 - 5, 0, 12]) {
            cylinder(h = 3, d = 4, center = true);
