        // Main jaw body
        translate([0, 0, 0]) {
            cube([jaw_width, jaw_thickness, 15], center = true);
        }

        // Blade insert
        color(color_blade) {
            translate([-jaw_width/2 + 2, -jaw_thickness/2 - 0.5, -2.5]) {
                cube([jaw_width - 4, 1, 5]);
            }
        }

        // Pivot point
        translate([0, 0, 0]) {
            cylinder(h = jaw_thickness + 4, d = 6, center = true);
        }

        // Spring mount
        translate([jaw_width/2 - 5, 0, 3]) {
            cylinder(h = 3, d = 4, center = true);
        }

        // Handle connector
        translate([-jaw_width/2 - 5, 0, -7.5]) {
            rotate([0, 90, 0]) {
                cylinder(h = 10, d = 8, center = true);
            }
        }
    }
