// Realistic Shear Tool - Medium Complexity
// Designed for 3D printing - approximately 80mm length

// Main parameters
handle_length = 35;
handle_diameter = 8;
blade_length = 45;
blade_width = 15;
blade_thickness = 2;
pivot_radius = 3;
grip_radius = 1.5;

// Colors
handle_color = [0.2, 0.2, 0.2]; // Dark gray rubber
metal_color = [0.7, 0.7, 0.8]; // Light gray metal
blade_color = [0.9, 0.9, 0.9]; // Silver blade

module grip_pattern(circles = 6) {
    // Non-slip grip pattern on handles
    for(i = [0:circles-1]) {
        rotate([0, 0, i * 60]) 
            translate([0, handle_diameter/2 + 0.5, 0])
                cylinder(h=0.5, r=grip_radius, $fn=16);
    }
}

module handle() {
    color(handle_color) {
        // Main handle body
        difference() {
            cylinder(h=handle_length, r=handle_diameter/2, $fn=32);
            // Hollow center for weight reduction
            cylinder(h=handle_length-2, r=handle_diameter/2-2, $fn=32);
        }

        // Grip rings
        for(z = [5, 10, 15, 20, 25, 30]) {
            translate([0, 0, z]) {
                cylinder(h=2, r=handle_diameter/2+1, $fn=32);
            }
        }

        // Finger indentations
        rotate([0, 90, 0]) {
            translate([0, 0, -handle_length/2]) {
                for(i = [0:3]) {
                    translate([0, 0, i*7+7]) {
                        sphere(r=handle_diameter/2+0.5);
                    }
                }
            }
        }
    }
}

module blade() {
    color(blade_color) {
        // Main blade body
        difference() {
            // Tapered blade
            hull() {
                translate([0, 0, 0]) cube([blade_length, blade_thickness, blade_width]);
                translate([blade_length-5, 0, 0]) cube([5, blade_thickness, blade_width-4]);
            }

            // Cutting edge bevel
            translate([0, blade_thickness/2, 0])
                rotate([0, -15, 0])
                    cube([blade_length+5, blade_thickness*2, blade_width*2]);
        }

        // Blade spine reinforcement
        translate([0, 0, blade_width/2-1]) {
            cube([blade_length, blade_thickness+1, 2]);
        }
    }
}

module finger_guard() {
    color(metal_color) {
        translate([0, 0, handle_length/2-5]) {
            difference() {
                cylinder(h=3, r=handle_diameter/2+2, $fn=32);
                cylinder(h=4, r=handle_diameter/2-1, $fn=32);
            }
        }
    }
}

module pivot_mechanism() {
    color(metal_color) {
        // Pivot cylinder
        cylinder(h=blade_thickness+4, r=pivot_radius, $fn=32);

        // Pivot head
        translate([0, 0, blade_thickness/2+2]) {
            cylinder(h=2, r=pivot_radius+1, $fn=32);
        }

        // Thread detail
        for(i = [0:5]) {
            translate([0, 0, i*0.5-1.5]) {
                cylinder(h=0.3, r=pivot_radius+0.2, $fn=32);
            }
        }
    }
}

// Assemble first handle and blade
translate([0, 0, 0]) {
    handle();
    translate([0, 0, handle_length/2+blade_width/2]) {
        rotate([0, 90, 0]) {
            blade();
        }
    }
    translate([0, 0, handle_length/2-2]) {
        finger_guard();
    }
    translate([0, handle_thickness/2+2, handle_length/2+blade_width/2]) {
        pivot_mechanism();
    }
}

// Assemble second handle and blade (mirrored)
translate([0, blade_thickness+4, 0]) {
    rotate([180, 0, 0]) {
        handle();
        translate([0, 0, handle_length/2+blade_width/2]) {
            rotate([0, 90, 0]) {
                blade();
            }
        }
        translate([0, 0, handle_length/2-2]) {
            finger_guard();
        }
        translate([0, -handle_thickness/2-2, handle_length/2+blade_width/2]) {
            pivot_mechanism();
        }
    }
}

// Lathe Cutting Tool (Medium Complexity Realistic Style)
// Overall dimensions: ~80mm x 25mm x 25mm

// Color definitions
tool_steel = [0.5, 0.5, 0.5];      // Gray tool steel
handle_color = [0.2, 0.1, 0.05];   // Dark wood handle
insert_color = [0.8, 0.8, 0.2];    // Yellow carbide insert
setscrew_color = [0.3, 0.3, 0.3];  // Dark gray screws

// Main module
module lathe_tool() {
    // Tool holder body
    difference() {
        union() {
            // Main shank
            color(tool_steel) {
                cube([60, 20, 20], center=true);
            }

            // Tool head
            translate([30, 0, 0]) {
                rotate([0, 0, 45]) {
                    color(tool_steel) {
                        cube([20, 20, 20], center=true);
                    }
                }
            }
        }

        // Center hole for clamping
        rotate([0, 90, 0]) {
            cylinder(h=22, r=4.5, center=true);
        }
    }

    // Carbide insert pocket
    translate([35, 0, 10]) {
        rotate([0, -5, 45]) {
            color(insert_color) {
                cube([8, 2.5, 6], center=true);
            }
        }
    }

    // Cutting edge
    translate([35, 0, 10]) {
        rotate([0, -5, 45]) {
            translate([4, 0, 3]) {
                color(insert_color) {
                    cube([1, 2.5, 1], center=true);
                }
            }
        }
    }

    // Clamp screw
    translate([32, 0, 10]) {
        color(setscrew_color) {
            cylinder(h=8, r=2.5, $fn=6);
        }
    }

    // Handle grip
    translate([-35, 0, 0]) {
        difference() {
            color(handle_color) {
                cylinder(h=30, r=10, center=true);
            }

            // Grip knurling
            for(i = [0:5:30]) {
                translate([0, 0, -15 + i]) {
                    rotate([0, 0, 45]) {
                        cube([18, 2, 1], center=true);
                    }
                }
            }
        }
    }

    // Set screws
    for(pos = [[-30, 0, 0], [-20, 0, 0]]) {
        translate(pos) {
            color(setscrew_color) {
                cylinder(h=5, r=3, $fn=6);
            }
        }
    }

    // Rounded edges on shank
    translate([0, 0, -10]) {
        color(tool_steel) {
            cube([60, 18, 1], center=true);
        }
    }

    // Identification groove
    translate([-10, 0, -10.5]) {
        rotate([90, 0, 0]) {
            cylinder(h=0.5, r=8, $fn=3);
        }
    }
}

