color_metal  = "Silver";
color_pepper = "DarkGoldenrod";

// --------------------
// Pepper-mill body
// --------------------
module body() {
    color(color_body)
    difference() {
        // Main cylinder
        cylinder(h = 60, d = 45, center = false);

        // Hollow chamber for peppercorns
        translate([0, 0, 5])
            cylinder(h = 45, d = 35);

        // Filling hole (top)
        translate([0, 0, 55])
            cylinder(h = 5, d = 20);
    }
}

// --------------------
// Metal adjustment knob
// --------------------
module knob() {
    color(color_metal)
    union() {
        // Knob cylinder
        translate([0, 0, 60])
            cylinder(h = 10, d = 48);

        // Grip ridges
        for (i = [0 : 30 : 330]) {
            rotate([0, 0, i])
                translate([22, 0, 60])
                    cube([2, 2, 10], center = true);
        }
    }
}

// --------------------
// Crank arm with handle
// --------------------
module crank() {
    color(color_metal)
    union() {
        // Arm
        translate([0, 0, 65])
            rotate([90, 0, 0])
                cylinder(h = 25, d = 6);

        // Handle knob
        translate([0, 25, 65])
            sphere(d = 12);
    }
}

// --------------------
// Grinding mechanism (simplified burr)
// --------------------
module burr() {
    color(color_metal)
    difference() {
        cylinder(h = 15, d = 34);
        // Teeth
        for (i = [0 : 15 : 345]) {
            rotate([0, 0, i])
                translate([15, 0, 0])
                    cube([2, 1, 15], center = true);
        }
    }
}

// --------------------
// Peppercorns (decorative)
// --------------------
module peppercorns() {
    color(color_pepper)
    for (i = [0 : 5]) {
        a = i * 60;
        r = 10 + (i % 2) * 5;
        translate([r * cos(a), r * sin(a), 25 + (i % 3) * 10])
            sphere(d = 4);
    }
}

// --------------------
// Assembly
// --------------------
body();
knob();
crank();
translate([0, 0, 5]) burr();
peppercorns();

// Wood Shaper Tool - OpenSCAD
// Realistic style, medium complexity

$fn = 50;

// Colors
color_base = [0.2, 0.2, 0.2];  // Dark gray
color_handle = [0.5, 0.3, 0.1];  // Wood brown
color_blade = [0.7, 0.7, 0.7];  // Steel gray
color_screw = [0.8, 0.8, 0.8];  // Silver
color_guard = [0.3, 0.3, 0.3];  // Dark gray

// Main body dimensions
body_length = 80;
body_width = 35;
body_height = 25;
handle_diameter = 15;
handle_length = 60;

// Blade dimensions
blade_width = 25;
blade_height = 8;
blade_thickness = 3;

// Guard dimensions
guard_length = 40;
guard_width = 8;
guard_height = 15;

module shaper_body() {
    color(color_base)
    union() {
        // Main body block
        translate([-body_length/2, -body_width/2, -body_height/2])
            cube([body_length, body_width, body_height]);

        // Front slope
        translate([-body_length/2 - 10, -body_width/2, -body_height/2])
            rotate([0, 0, 20])
            cube([15, body_width, body_height]);

        // Top ridge
        translate([-body_length/2 + 10, -body_width/2, body_height/2])
            cube([body_length - 20, body_width, 3]);

        // Mounting holes
        for(i = [-1, 1]) {
            translate([body_length/2 - 15, i * 8, 0])
                cylinder(h = body_height + 1, r = 2, center = true);
            translate([body_length/2 - 25, i * 8, 0])
                cylinder(h = body_height + 1, r = 2, center = true);
        }
    }
}

module handle() {
    color(color_handle)
    union() {
        // Main handle
        translate([body_length/2 + handle_length/2, 0, 0])
            rotate([0, 90, 0])
            cylinder(h = handle_length, r = handle_diameter/2, center = true);

        // Handle grip rings
        for(i = [-2, -1, 0, 1, 2]) {
            translate([body_length/2 + 10 + i * 10, 0, 0])
                rotate([0, 90, 0])
                cylinder(h = 2, r1 = handle_diameter/2 + 1, r2 = handle_diameter/2 + 1, center = true);
        }

        // End cap
        translate([body_length/2 + handle_length, 0, 0])
            sphere(r = handle_diameter/2 + 1);
    }
}

module blade_assembly() {
    color(color_blade)
    difference() {
        // Main blade
        translate([-body_length/2 - 20, -blade_width/2, -blade_height/2])
            cube([40, blade_width, blade_height]);

        // Cutting edge profile
        translate([-body_length/2 - 25, -blade_width/2 - 1, -blade_height/2 - 1])
            cube([10, blade_width + 2, blade_height + 2]);

        // Mounting slot
        translate([-body_length/2 - 15, -3, -blade_height/2])
            cube([6, 6, blade_height + 1]);
    }

    // Blade screws
    color(color_screw)
    for(i = [-1, 1]) {
        translate([-body_length/2 - 12, i * 8, 0])
            cylinder(h = blade_thickness + 2, r = 2, center = true);
        translate([-body_length/2 - 12, i * 8, -blade_thickness/2 - 1])
            sphere(r = 2);
    }
}

module safety_guard() {
    color(color_guard)
    difference() {
        // Main guard
        translate([-body_length/2 - guard_length/2, -guard_width/2 - blade_width/2 - 5, -guard_height/2])
            cube([guard_length, guard_width, guard_height]);

        // Blade slot
        translate([-body_length/2 - 25, -blade_width/2 - 10, -blade_height/2 - 1])
            cube([20, 10, blade_height + 2]);

        // Mounting holes
        for(i = [-1, 1]) {
            translate([-body_length/2 - 10, -guard_width/2 - blade_width/2 - 5, i * 5])
                cylinder(h = guard_height + 1, r = 1.5, center = true);
        }
    }
}

// Assembly
shaper_body();
handle();
blade_assembly();
safety_guard();

