        color(color_red)
        translate([35, 0, 0])
            cylinder(h=2, r=2.5, $fn=16);

        // Power cable entry
        color(color_dark)
        translate([-25, 0, 0])
            rotate([90, 0, 0])
                cylinder(h=10, r=3, $fn=16);

        // Trigger
