arbor_len = 8;

// Colors
color_handle = "DarkSlateGray";
color_guard = "Silver";
color_wheel = "LightSlateGray";
color_arbor = "Gray";
color_screw = "Black";

// Main assembly
difference() {
    union() {
        // Handle with grip
        translate([0, 0, handle_len/2])
            color(color_handle)
            handle_assembly(handle_len, handle_dia);

        // Safety guard
        translate([0, -wheel_dia/2 - guard_thick/2 - 5, handle_len - 20])
            color(color_guard)
            guard();

        // Cutting wheel
        translate([0, 0, handle_len - 20])
            rotate([90, 0, 0])
            color(color_wheel)
            cutting_wheel();

        // Arbor spacer
        translate([0, 0, handle_len - 20])
            rotate([90, 0, 0])
            color(color_arbor)
            cylinder(d = arbor_dia + 4, h = 3, center = true);
    }

    // Central hole through wheel and arbor
    translate([0, 0, handle_len - 20])
        rotate([90, 0, 0])
        cylinder(d = arbor_dia, h = arbor_len + 10, center = true);
}

// Screw head on the arbor
translate([0, arbor_len/2 + 1, handle_len - 20])
    rotate([90, 0, 0])
    color(color_screw)
    cylinder(d1 = arbor_dia + 2, d2 = arbor_dia, h = 2, $fn = 6);

module handle_assembly(len, dia) {
    difference() {
        // Main handle body
        cylinder(d = dia, h = len);

        // Grip rings
        for (i = [1 : 7 : len - 7]) {
            translate([0, 0, i])
                cylinder(d = dia + 1, h = 3);
        }

        // Flat sides for grip
        for (a = [0 : 120 : 240]) {
            rotate([0, 0, a])
                translate([dia/2 - 1, 0, len/2])
                    cube([dia/2, dia * 0.8, len], center = true);
        }
    }
}

module guard() {
    difference() {
        // Main guard disc
        cylinder(d = wheel_dia + 20, h = guard_thick);

        // Cutout for wheel
        translate([0, 0, -1])
            cylinder(d = wheel_dia + 2, h = guard_thick + 2);

        // Mounting holes
        for (a = [45 : 90 : 315]) {
            rotate([0, 0, a])
                translate([wheel_dia/2 + 6, 0, -1])
                    cylinder(d = 3, h = guard_thick + 2);
        }
    }
}

module cutting_wheel() {
    difference() {
        // Main wheel
        cylinder(d = wheel_dia, h = wheel_thick, center = true);

        // Reinforcement web pattern
        for (r = [wheel_dia * 0.3, wheel_dia * 0.6]) {
            rotate_extrude()
                translate([r, 0, 0])
                    square([wheel_thick * 0.8, wheel_thick * 0.3], center = true);
        }

        // Lightening holes
        for (a = [0 : 30 : 330]) {
            rotate([0, 0, a])
                translate([wheel_dia * 0.4, 0, 0])
                    cylinder(d = 3, h = wheel_thick + 0.1, center = true);
        }
    }
}

