table_length = 80;
table_width = 50;
table_height = 25;

// Colors
table_color = "BurlyWood";
vise_color = "SlateGray";
handle_color = "DarkRed";
measure_color = "Black";

// Main table module
module table_base() {
    color(table_color) {
        // Main tabletop with rounded edges
        difference() {
            minkowski() {
                cube([table_length, table_width, table_height], center=true);
                sphere(2);
            }
            translate([0, 0, table_height/2 + 1])
                cube([table_length - 4, table_width - 4, table_height + 2], center=true);
        }

        // Table legs
        for(x = [-1, 1])
        for(y = [-1, 1]) {
            translate([x * (table_length/2 - 8), y * (table_width/2 - 8), -table_height/2 - 10])
                cylinder(h=20, d=6, center=true);
        }
    }
}

// Vise jaw module
module vise_jaw(position) {
    color(vise_color) {
        translate([position, 0, 0]) {
            // Jaw body
            cube([8, table_width - 20, 15], center=true);

            // Serrated grip surface
            for(i = [-6:2:6]) {
                translate([position > 0 ? 3.9 : -3.9, i, 0])
                    cube([0.5, 1.5, 13], center=true);
            }
        }
    }
}

// Vise handle module
module vise_handle(position) {
    color(handle_color) {
        translate([position, 0, 8]) {
            rotate([0, 90, 0]) {
                // Handle bar
                cylinder(h=30, d=3, center=true);

                // Handle knob
                translate([15, 0, 0])
                    sphere(4);
                translate([-15, 0, 0])
                    sphere(4);

                // Handle grip
                for(i = [-12:3:12]) {
                    translate([i, 0, 1.5])
                        cylinder(h=2, d=3.5);
                }
            }
        }
    }
}

// Measurement scale module
module measurement_scale() {
    color(measure_color) {
        // Top scale markings
        for(i = [0:5:table_length - 10]) {
            translate([-table_length/2 + 5 + i, table_width/2 - 2, table_height/2 + 0.1])
                cube([0.5, 4, 0.5]);
        }

        // Side scale markings
        for(i = [0:5:table_width - 10]) {
            translate([table_length/2 - 2, -table_width/2 + 5 + i, table_height/2 + 0.1])
                cube([4, 0.5, 0.5]);
        }
    }
}

// Tool tray module
module tool_tray() {
    color("DarkGray") {
        translate([0, table_width/2 - 10, -table_height/2 - 5]) {
            difference() {
                cube([table_length - 20, 15, 5], center=true);
                translate([0, 0, 2.5])
                    cube([table_length - 22, 13, 6], center=true);
            }
        }
    }
}

// Assembly
table_base();
vise_jaw(table_length/2 - 15);
vise_jaw(-table_length/2 + 15);
vise_handle(table_length/2 - 20);
measurement_scale();
tool_tray();

// Band Tool - Realistic Style with Medium Complexity
// A recognizable band clamping tool with handle, ratchet mechanism and strap

// Color definitions
$fn = 50;

// Main assembly
module bandTool() {
    // Handle assembly
    color("dimgray") handle();

    // Ratchet mechanism housing
    color("silver") ratchetHousing();

    // Strap and buckle system
    color("black") strapSystem();

    // Adjustment wheel
    color("silver") adjustmentWheel();
}

// Handle with grip texture
module handle() {
    translate([0, 0, 15]) {
        // Main handle body
        difference() {
            cylinder(h=80, d=25, center=true);
            // Hollow interior
            cylinder(h=82, d=18, center=true);
        }

        // Grip rings
        for(i = [-30:8:30]) {
            translate([0, 0, i]) {
                difference() {
                    cylinder(h=3, d=28, center=true);
                    cylinder(h=4, d=25, center=true);
                }
            }
        }
    }
}

// Ratchet mechanism housing
module ratchetHousing() {
    translate([0, 0, 0]) {
        // Main housing block
        cube([40, 25, 30], center=true);

        // Strap entry slot
        translate([0, 0, 10]) {
            cube([38, 22, 8], center=true);
        }

        // Ratchet teeth
        for(i = [0:5]) {
            rotate([0, 0, i * 12 - 30]) {
                translate([18, 0, 0]) {
                    cube([3, 4, 8], center=true);
                }
            }
        }
    }
}

// Strap and buckle system
module strapSystem() {
    // Main strap body
    translate([0, 0, -25]) {
        // Strap thickness
        linear_extrude(height=2) {
            polygon(points=[
                [-20, -8], [20, -8], [20, 8], [-20, 8]
            ]);
        }

        // Strap holes
        for(i = [-15:5:15]) {
            translate([i, 0, -25]) {
                cylinder(h=3, d=3, center=true);
            }
        }

        // Buckle frame
        translate([22, 0, -25]) {
            difference() {
                // Outer frame
                cube([8, 16, 3], center=true);
                // Inner opening
                cube([6, 12, 4], center=true);
            }
        }
    }
}

// Adjustment wheel
module adjustmentWheel() {
    translate([0, 20, 0]) {
        // Wheel body
        cylinder(h=8, d=20, center=true);

        // Spoke details
        for(i = [0:45:315]) {
            rotate([0, 0, i]) {
                translate([7, 0, 0]) {
                    cube([12, 2, 6], center=true);
                }
            }
        }

        // Central knob
        translate([0, 0, 5]) {
            cylinder(h=5, d=8);
        }
    }
}

// Render the complete tool
bandTool();

// Scroll Tool - Realistic Style, Medium Complexity
// A handheld scroll tool with handle and scroll mechanism

// Overall dimensions (mm)
scroll_length = 80;
scroll_diameter = 12;
handle_length = 40;
handle_diameter = 15;

// Colors
wood_color = [0.6, 0.4, 0.2];
metal_color = [0.7, 0.7, 0.7];
paper_color = [0.9, 0.85, 0.75];

// Main assembly
union() {
    // Wooden handle
    color(wood_color) {
        // Handle grip
        translate([0, 0, -handle_length/2]) {
            cylinder(h = handle_length, d = handle_diameter, $fn = 32);
        }

        // Handle end cap
        translate([0, 0, -handle_length-5]) {
            cylinder(h = 5, d = handle_diameter + 2, $fn = 32);
        }

        // Handle grip rings
        for(i = [1:3]) {
            translate([0, 0, -handle_length + i*10]) {
                difference() {
                    cylinder(h = 2, d = handle_diameter + 1, $fn = 32);
                    cylinder(h = 3, d = handle_diameter - 2, $fn = 32);
                }
            }
        }
    }

    // Metal scroll mechanism
    color(metal_color) {
        // Main scroll rod
        translate([0, 0, handle_length/2]) {
            rotate([90, 0, 0]) {
                cylinder(h = scroll_length, d = scroll_diameter, $fn = 32);

                // End caps
                translate([0, 0, scroll_length/2]) {
                    cylinder(h = 3, d = scroll_diameter + 4, $fn = 32);
                }
                translate([0, 0, -scroll_length/2]) {
                    cylinder(h = 3, d = scroll_diameter + 4, $fn = 32);
                }

                // Scroll knobs
                translate([0, scroll_length/2 + 3, 0]) {
                    rotate([0, 90, 0]) {
                        cylinder(h = 8, d = 8, $fn = 32);
                    }
                }
                translate([0, -scroll_length/2 - 3, 0]) {
                    rotate([0, 90, 0]) {
                        cylinder(h = 8, d = 8, $fn = 32);
                    }
                }
            }
        }
    }

    // Scroll paper
    color(paper_color) {
        difference() {
            // Main scroll paper
            translate([0, 0, handle_length/2 + 2]) {
                rotate([90, 0, 0]) {
                    rotate_extrude(angle = 360, $fn = 64) {
                        translate([scroll_diameter/2 + 0.5, 0, 0]) {
                            square([0.5, scroll_length - 10]);
                        }
                    }
                }
            }

            // Paper edges for realism
            translate([0, 0, handle_length/2 + 2]) {
                rotate([90, 0, 0]) {
                    for(i = [0:5:scroll_length-10]) {
                        rotate([0, i, 0]) {
                            translate([scroll_diameter/2 + 1, 0, 0]) {
                                cube([0.3, 2, 2], center = true);
                            }
                        }
                    }
                }
            }
        }
    }

    // Handle to scroll transition
    color(metal_color) {
        translate([0, 0, handle_length/2 - 2]) {
            cylinder(h = 4, d = handle_diameter - 2, $fn = 32);
        }
    }

    // Decorative bands on scroll ends
    color(metal_color) {
        for(pos = [scroll_length/2 - 5, -scroll_length/2 + 5]) {
            translate([0, pos, handle_length/2]) {
                rotate([90, 0, 0]) {
                    cylinder(h = 2, d = scroll_diameter + 2, $fn = 32);
                }
            }
        }
    }
}

// Radial arm saw - realistic medium complexity model
// Overall dimensions: ~80mm wide x ~60mm deep x ~100mm tall

// Main colors
color_table = [0.2, 0.2, 0.2];        // Dark gray
color_metal = [0.5, 0.5, 0.5];       // Silver
color_blade = [0.7, 0.7, 0.7];       // Light metallic
color_grip = [0.1, 0.1, 0.1];        // Black
color_safety = [0.8, 0.1, 0.1];      // Red safety features

// Base table with T-tracks
module base_table() {
    color(color_table)
    union() {
        // Main table surface
        cube([80, 60, 5], center=true);

        // T-tracks for miter gauge
        for(i = [-30:15:30]) {
            translate([i, 0, 2.5])
                cube([8, 58, 3], center=true);
            translate([i, 0, 3])
                cube([4, 58, 2], center=true);
        }

        // Front edge fence
        translate([0, 29, 2.5])
            cube([78, 3, 5], center=true);
    }
}

// Radial arm assembly
module radial_arm() {
    color(color_metal)
    union() {
        // Vertical column
        translate([-35, 0, 30])
            rotate([0, 90, 0])
                cylinder(h=70, r=6, center=true);

        // Horizontal arm
        translate([0, 0, 60])
            cube([75, 8, 8], center=true);

        // Support brackets
        for(side = [-1, 1]) {
            translate([-35, side*3, 45])
                rotate([0, -45*side, 0])
                    cube([20, 3, 3]);
        }
    }
}

// Saw motor housing
module motor_housing() {
    color(color_metal)
    union() {
        // Main motor body
        translate([20, 0, 60])
            rotate([0, 90, 0])
                cylinder(h=25, r=12, center=true);

        // Motor end cap
        translate([7, 0, 60])
            rotate([0, 90, 0])
                cylinder(h=5, r=10, center=true);

        // Handle/grip
        color(color_grip)
        translate([30, 0, 60])
            rotate([0, 0, 90])
                rotate([90, 0, 0])
                    cylinder(h=15, r=4, center=true);
    }
}

// Circular saw blade with teeth
module blade() {
    color(color_blade)
    union() {
        // Main blade disc
        translate([7, 0, 60])
            rotate([0, 90, 0])
                cylinder(h=1.5, r=20, center=true);

        // Blade teeth (simplified as notches)
        for(i = [0:15:359]) {
            rotate([0, 90, i])
                translate([7, 0, 60])
                    cube([2, 20.5, 1.5], center=true);
        }

        // Arbor hole
        color(color_metal)
        translate([7, 0, 60])
            rotate([0, 90, 0])
                cylinder(h=2, r=3, center=true);
    }
}

// Blade guard
module blade_guard() {
    color(color_safety)
    difference() {
        // Main guard housing
        translate([7, 0, 60])
            rotate([0, 90, 0])
                cylinder(h=5, r=23, center=true);

        // Cutout for blade
        translate([7, 0, 60])
            rotate([0, 90, 0])
                cylinder(h=7, r=21, center=true);
    }
}

// Depth adjustment mechanism
module depth_adjust() {
    color(color_metal)
    union() {
        // Adjustment knob
        translate([35, 15, 60])
            rotate([90, 0, 0])
                cylinder(h=8, r=6, center=true);

        // Knurling on knob
        for(i = [0:30:359]) {
            rotate([0, 0, i])
                translate([35, 15, 60])
                    cube([1, 1, 8], center=true);
        }

        // Indicator scale
        color([1, 1, 1])
        translate([35, 15, 56])
            for(i = [0:3:12]) {
                rotate([0, 0, i*30-60])
                    translate([5, 0, 0])
                        cube([0.5, 0.5, 2], center=true);
            }
    }
}

// Assembly
base_table();
radial_arm();
motor_housing();
blade();
blade_guard();
depth_adjust();

// Panel Tool - Realistic Style with Medium Complexity
// A panel removal tool for automotive interior work

// Overall dimensions
width = 85;
height = 20;
depth = 12;

// Handle parameters
handle_width = 15;
handle_height = 12;
handle_length = 60;

// Blade parameters
blade_width = 5;
blade_thickness = 2;
blade_length = 25;
blade_angle = 30;

// Grip parameters
grip_radius = 7.5;
grip_depth = 1;
grip_spacing = 8;

// Color definitions
handle_color = [0.2, 0.2, 0.2];
blade_color = [0.8, 0.8, 0.8];
grip_color = [0.1, 0.1, 0.1];

module handle_grips() {
    // Create textured grip pattern
    for (i = [0:3]) {
        translate([0, 0, -handle_length/2 + grip_spacing + i * grip_spacing])
            rotate([90, 0, 0])
                difference() {
                    cylinder(h=grip_radius*2, r=grip_radius, $fn=20);
                    cylinder(h=grip_radius*2+0.1, r=grip_radius-grip_depth, $fn=20);
                }
    }
}

module handle_body() {
    color(handle_color)
    difference() {
        // Main handle shape
        translate([0, 0, -handle_length/2])
            scale([1, 0.9, 1])
                cylinder(h=handle_length, r=handle_width/2, $fn=32);

        // Grip recess
        translate([0, 0, -handle_length/2 + 5])
            cylinder(h=handle_length-10, r=handle_width/2 - 2, $fn=32);
    }

    // Add grip texture
    color(grip_color) handle_grips();

    // Add end cap
    translate([0, 0, handle_length/2])
        cylinder(h=3, r=handle_width/2, $fn=32);
}

module blade_body() {
    color(blade_color) {
        // Main blade
        translate([0, -blade_length/2, 0])
            rotate([0, 0, blade_angle])
                cube([blade_width, blade_length, blade_thickness], center=true);

        // Tapered tip
        translate([0, -blade_length + 2, 0])
            rotate([0, 0, blade_angle])
                scale([1, 0.3, 1])
                    cube([blade_width, 4, blade_thickness], center=true);

        // Reinforcement ridge
        translate([0, -blade_length/2, blade_thickness/2 + 0.5])
            rotate([0, 0, blade_angle])
                cube([blade_width - 1, blade_length - 5, 1], center=true);
    }
}

module transition_joint() {
    // Curved transition from handle to blade
    color(handle_color)
    union() {
        // Main joint body
        translate([0, -handle_width/4, 0])
            rotate([0, 90, 0])
                cylinder(h=handle_width/2, r1=handle_width/2, r2=blade_width/2, $fn=20);

        // Reinforcement bracket
        translate([0, -handle_width/2 - 2, 0])
            rotate([0, 90, 0])
                cylinder(h=handle_width/2, r1=handle_width/4, r2=blade_width/4, $fn=20);
    }
}

module panel_tool() {
    // Assemble all parts
    handle_body();

    transition_joint();

    // Position blade correctly
    translate([0, -handle_width/2 - blade_length/2, 0])
        blade_body();

    // Add manufacturer's mark
    translate([0, -handle_width/2 - 5, handle_height/2])
        rotate([90, 0, 0])
            linear_extrude(height=0.5)
                text("PANEL", size=3, halign="center", valign="center", font="Arial:style=Bold");
}

// Render the complete tool
panel_tool();

// Beam Tool - OpenSCAD Model (Realistic Style, Medium Complexity)

// Overall dimensions (mm)
length = 85;
width = 12;
height = 25;

// Handle parameters
handle_length = 40;
handle_diameter = 10;
grip_length = 35;
grip_ring_width = 2;
grip_ring_spacing = 5;

// Blade parameters
blade_length = 45;
blade_width = 12;
blade_thickness = 1.5;
blade_angle = 30;

// Pivot mechanism
pivot_radius = 3;
pivot_height = 8;
spring_radius = 1.5;
spring_coils = 5;

module handle_with_grip() {
    difference() {
        // Main handle cylinder
        color([0.2, 0.2, 0.2])
        cylinder(h=handle_length, d=handle_diameter, $fn=32);

        // Grip rings
        for(i = [0:4]) {
            translate([0, 0, grip_ring_spacing + i * (grip_ring_width + grip_ring_spacing)])
                color([0.1, 0.1, 0.1])
                cylinder(h=grip_ring_width, d=handle_diameter + 1, $fn=32);
        }
    }

    // Grip texture rings
    for(i = [0:6]) {
        translate([0, 0, 2 + i * 5])
            difference() {
                cylinder(h=1, d=handle_diameter + 0.5, $fn=32);
                cylinder(h=1.1, d=handle_diameter - 1, $fn=32);
            }
    }
}

module blade() {
    // Main blade body
    translate([0, 0, blade_length/2])
        rotate([0, 90, 0])
        difference() {
            // Tapered blade
            hull() {
                translate([0, 0, -blade_width/2])
                    cube([blade_length, blade_thickness, blade_width], center=true);
                translate([blade_length/2 - 5, 0, 0])
                    cube([10, blade_thickness, blade_width * 0.7], center=true);
            }

            // Cutting edge bevel
            translate([0, blade_thickness/2, 0])
                rotate([0, 0, -blade_angle])
                cube([blade_length * 2, blade_thickness * 2, blade_width * 2], center=true);
        }

    // Blade markings
    color([0.8, 0.8, 0.8])
    for(i = [1:4]) {
        translate([blade_length - i * 8, 0, blade_width/2 + 0.1])
            cube([0.5, 0.2, 2], center=true);
    }
}

module pivot_mechanism() {
    // Pivot pin
    color([0.6, 0.6, 0.6])
    translate([0, 0, 0])
        cylinder(h=pivot_height, r=pivot_radius, $fn=16);

    // Spring mechanism
    color([0.7, 0.7, 0.7])
    for(i = [0:spring_coils-1]) {
        translate([0, 0, 2 + i * 0.8])
            rotate([0, 0, i * 30])
            cylinder(h=0.8, r1=spring_radius, r2=spring_radius * 0.9, $fn=8);
    }
}

// Assemble the beam tool
union() {
    // Handle assembly
    translate([-handle_length/2, 0, 0])
        rotate([90, 0, 0])
        handle_with_grip();

    // Pivot mechanism
    translate([handle_length/2 - 2, 0, 0])
        rotate([90, 0, 0])
        pivot_mechanism();

    // Blade assembly
    translate([handle_length/2 + 2, 0, 0])
        rotate([90, 0, 0])
        blade();

    // Safety lock
    color([0.3, 0.3, 0.3])
    translate([handle_length/2 - 5, handle_diameter/2 + 1, 0])
        rotate([0, 90, 0])
        cube([8, 3, 2], center=true);

    // Thumb rest
    color([0.4, 0.4, 0.4])
    translate([handle_length/2 + 10, 0, handle_diameter/2 + 2])
        rotate([0, 0, 45])
        cube([6, 2, 4], center=true);
}

