// Flooring Tapping Block (for laminate flooring installation)
// Realistic style, medium complexity

// Parameters
block_length = 90;
block_width = 40;
block_height = 20;
handle_length = 70;
handle_width = 25;
handle_height = 15;
grip_radius = 12;
lip_height = 8;
lip_width = 3;
magnet_diameter = 6;
magnet_depth = 3;
text_depth = 0.5;

module main_block() {
    difference() {
        // Main block body
        color("lightgray")
        cube([block_length, block_width, block_height], center=true);

        // Rounded edges on top
        translate([0, 0, block_height/2])
        cylinder(h=block_height+1, r=2, center=true);

        // Bottom lip for flooring edge
        translate([block_length/2 - lip_width/2, 0, -block_height/2 - lip_height/2])
        color("darkgray")
        cube([lip_width, block_width - 4, lip_height], center=true);

        // Magnet holes for alignment
        for(x = [-30, 0, 30]) {
            translate([x, 0, block_height/2 - magnet_depth/2])
            cylinder(h=magnet_depth+1, d=magnet_diameter, center=true);
        }

        // Text engraving
        translate([-block_length/2 + 10, 0, block_height/2 - text_depth/2])
        linear_extrude(height=text_depth+0.1)
        text("TAP", size=8, halign="center", valign="center");
    }
}

module handle() {
    union() {
        // Main handle body
        translate([0, 0, block_height/2 + handle_height/2])
        color("darkgray")
        cube([handle_length, handle_width, handle_height], center=true);

        // Rounded handle edges
        translate([handle_length/2 - 5, 0, block_height/2 + handle_height/2])
        cylinder(h=handle_width, r=5, center=true, $fn=20);

