handle_diameter = 25;
grip_thickness = 3;
grip_grooves = 6;

// Tang parameters
tang_length = 25;
tang_width = 10;
tang_thickness = 4;

// Rivet parameters
rivet_diameter = 5;
rivet_height = 2;
rivets = 2;

module blade() {
    color([0.65, 0.65, 0.65])  // Steel grey blade
    difference() {
        // Main curved blade
        translate([blade_length/2, 0, blade_thickness/2])
        scale([1, blade_width/blade_length, blade_thickness/blade_length])
        sphere(d=blade_length);

        // Flatten bottom and taper edges
        translate([-5, 0, -1])
        cube([blade_length*2, blade_width*2, blade_thickness+2]);

        // Taper front edge
        translate([blade_length, -blade_width/2, blade_thickness-1])
        rotate([0, 0, 15])
        cube([blade_length/2, blade_width, blade_thickness+2]);

        // Rounded corners
        translate([0, blade_width/2, 0])
        cylinder(h=blade_thickness+2, r=5);
        translate([0, -blade_width/2, 0])
        cylinder(h=blade_thickness+2, r=5);
    }
}

module tang() {
    color([0.6, 0.6, 0.6])  // Steel tang
    difference() {
        // Main tang body
        translate([blade_length - tang_length/2, 0, tang_thickness/2])
        cube([tang_length, tang_width, tang_thickness], center=true);

        // Taper towards handle
        for(i=[0:1]) {
            mirror([0, i==0 ? 1 : -1, 0])
            translate([blade_length - tang_length*0.75, tang_width/2, 0])
            rotate([0, 0, -20])
            cube([tang_length/2, tang_width, tang_thickness+2]);
        }
    }
}

module handle() {
    // Main handle body
    color([0.4, 0.2, 0.1])  // Wood color
    translate([blade_length + handle_length/2 - handle_overlap, 0, handle_diameter/2])
    rotate([90, 0, 0])
    cylinder(h=handle_length, d=handle_diameter, $fn=32);

    // Grip grooves
    color([0.3, 0.15, 0.08])
    for(i=[0:grip_grooves-1]) {
        rotate([0, 0, i*360/grip_grooves])
        translate([0, 0, blade_length + handle_length/2 - handle_overlap])
        rotate([90, 0, 0])
        cylinder(h=handle_length*0.8, d=handle_diameter-grip_thickness*2, $fn=6);
    }

    // End cap
    color([0.5, 0.5, 0.5])
    translate([blade_length + handle_length - handle_overlap, 0, handle_diameter/2])
    rotate([90, 0, 0])
    cylinder(h=5, d=handle_diameter+2, $fn=32);
}

module rivets() {
    color([0.7, 0.7, 0.7])  // Steel rivets
    for(i=[0:rivets-1]) {
        translate([blade_length - tang_length/2 + tang_length*(i+1)/(rivets+1), 
                  0, 
                  handle_diameter/2 + rivet_height/2])
        cylinder(h=rivet_height, d=rivet_diameter, $fn=16);
    }
}

// Assembly
union() {
    blade();
    tang();
    handle();
    rivets();
}

