module flutes() {
    // Generate 4 cutting flutes with helix angle
    for (i = [0:3]) {
        rotate([0, 0, i * 90]) {
            translate([0, 0, -flute_length/2]) {
                rotate([0, 15, 0]) {
                    difference() {
                        cylinder(h=flute_length, d1=tool_diameter, d2=tool_diameter*0.9);
                        translate([0, -tool_diameter/2, -1]) {
                            cube([tool_diameter/2, tool_diameter/2, flute_length+2], center=false);
                        }
                    }
                }
            }
        }
    }
}

module cutting_edges() {
    // Sharp cutting edges at tool tip
    translate([0, 0, -tool_length/2]) {
        for (i = [0:3]) {
            rotate([0, 0, i * 90 + 45]) {
                translate([0, tool_diameter*0.3, 0]) {
                    rotate([0, -30, 0]) {
                        cube([0.5, tool_diameter*0.2, 3], center=true);
                    }
                }
            }
        }
    }
}

module tool_body() {
    // Main carbide tool body
    color(carbide_color) {
        union() {
            // Cutting portion with flutes
            translate([0, 0, -tool_length/2 + flute_length/2]) {
                flutes();
                cutting_edges();
            }

            // Shank portion
            translate([0, 0, -tool_length/2 + flute_length + (tool_length-flute_length)/2]) {
                cylinder(h=tool_length-flute_length, d=shank_diameter);
            }

            // Tool tip sphere
            translate([0, 0, -tool_length/2]) {
                sphere(d=tool_diameter);
            }
        }
    }
}

module toolholder() {
    // ER collet chuck holder
    color(steel_color) {
        union() {
            // Main holder body
            cylinder(h=holder_length, d=holder_diameter);

            // Collet nut
            translate([0, 0, holder_length/2 - collet_length/2]) {
                color(black_color) {
                    difference() {
                        cylinder(h=collet_length, d=collet_diameter+4);
                        // Nut flats
                        for (i = [0:5]) {
                            rotate([0, 0, i * 60]) {
                                translate([collet_diameter/2 + 2, 0, 0]) {
                                    cube([5, 1, collet_length+0.1], center=true);
                                }
                            }
                        }
                    }
                }
            }

            // Taper section
            translate([0, 0, -holder_length/2 - 5]) {
                cylinder(h=10, d1=holder_diameter, d2=collet_diameter);
            }

            // Internal bore
            difference() {
                cylinder(h=holder_length+10, d=holder_diameter);
                translate([0, 0, -holder_length/2 - 5]) {
                    cylinder(h=holder_length+15, d=shank_diameter+1);
                }
            }
        }
    }
}

