// Digital Caliper - Realistic Style, Medium Complexity
// 3D printable digital caliper with main body, slider, jaws, and display

// Constants
$fn = 50;
body_length = 80;
body_width = 25;
body_height = 8;
jaw_length = 40;
jaw_width = 4;
thumb_wheel_radius = 8;
thumb_wheel_width = 4;
display_width = 15;
display_height = 8;
depth_rod_length = 20;

// Colors
color_body = [0.2, 0.2, 0.2];  // Dark gray
color_slider = [0.3, 0.3, 0.3];  // Medium gray
color_jaws = [0.6, 0.6, 0.6];  // Light gray
color_display = [0.1, 0.1, 0.1];  // Black
color_marks = [0.9, 0.9, 0.9];  // White
color_wheel = [0.4, 0.4, 0.4];  // Wheel color

// Main caliper body
module caliper_body() {
    difference() {
        // Main rectangular body
        color(color_body)
        cube([body_length, body_width, body_height], center = true);

        // Slot for slider
        translate([0, 0, -body_height/2 + 0.5])
            cube([body_length - 10, body_width - 6, body_height + 1], center = true);

        // Measurement marks - every 5mm
        for(i = [0:5:body_length]) {
            translate([i - body_length/2, body_width/2 - 1.5, -body_height/2 + 0.1])
                cube([0.5, 3, 0.2]);
        }
    }
}

// Sliding jaw assembly
module sliding_jaw() {
    translate([body_length/2 - 10, 0, 0]) {
        union() {
            // Slider body
            color(color_slider)
            translate([0, 0, -body_height/2 + 2])
                cube([20, body_width - 4, body_height - 1]);

            // Upper jaw
            color(color_jaws)
            translate([10, body_width/2 - jaw_width/2, 0])
                cube([jaw_length, jaw_width, body_height + 4]);

            // Lower jaw
            translate([10, -body_width/2, -body_height/2 - 2])
                cube([jaw_length, jaw_width, body_height + 4]);

            // Thumb wheel
            color(color_wheel)
            rotate([90, 0, 0])
                translate([0, -thumb_wheel_width/2, body_width/2 + thumb_wheel_radius])
                    cylinder(h = thumb_wheel_width, r = thumb_wheel_radius);

            // Display
            color(color_display)
            translate([-5, 0, body_height/2 + 1])
                cube([display_width, display_width, display_height/2]);
        }
    }
}

// Fixed jaw assembly
module fixed_jaw() {
    translate([-body_length/2, 0, 0]) {
        color(color_jaws)
        union() {
            // Upper fixed jaw
            translate([0, body_width/2 - jaw_width/2, 0])
                cube([jaw_length, jaw_width, body_height + 4]);

            // Lower fixed jaw
            translate([0, -body_width/2, -body_height/2 - 2])
                cube([jaw_length, jaw_width, body_height + 4]);

            // Depth rod
            translate([jaw_length/2, 0, -body_height/2 - depth_rod_length/2 - 1])
                cube([2, 2, depth_rod_length]);
        }
    }
}

// Assembly
union() {
    caliper_body();
    fixed_jaw();
    sliding_jaw();
}

