// OpenSCAD Alignment Tool - Precision Corner & Edge Alignment Guide
// Realistic style, medium complexity

$fn = 50;

// Main dimensions
base_length = 80;
base_width = 40;
base_height = 10;
wall_thickness = 3;
handle_length = 60;
handle_diameter = 12;

module base_with_guides() {
    // Main base plate
    difference() {
        color("silver") cube([base_length, base_width, base_height], center=true);

        // Weight reduction holes
        for(i = [-25, 0, 25]) {
            translate([i, 0, 0])
                cylinder(h=base_height+1, d=8, center=true);
        }
    }

    // Alignment ridges
    translate([0, base_width/2 - 1.5, 0])
        color("gray") cube([base_length, 3, base_height], center=true);

    translate([0, -base_width/2 + 1.5, 0])
        color("gray") cube([base_length, 3, base_height], center=true);

    // Reference edges with measurement marks
    for(i = [-35:5:35]) {
        if(i % 10 == 0) {
            translate([i, 0, base_height/2 + 1.5])
                color("black") cube([0.5, base_width, 3], center=true);
        } else {
            translate([i, 0, base_height/2 + 1.5])
                color("black") cube([0.3, base_width/2, 1.5], center=true);
        }
    }

    // Corner alignment blocks
    module corner_block(x_pos, y_pos) {
        translate([x_pos, y_pos, base_height/2 - 5]) {
            difference() {
                color("darkblue") cube([8, 8, 15], center=true);
                translate([3.5, 3.5, 0])
                    cylinder(h=16, d=3, center=true);
            }
        }
    }

    corner_block(base_length/2 - 4, base_width/2 - 4);
    corner_block(-base_length/2 + 4, base_width/2 - 4);
    corner_block(base_length/2 - 4, -base_width/2 + 4);
    corner_block(-base_length/2 + 4, -base_width/2 + 4);
}

module handle() {
    translate([-base_length/2 - handle_length/2, 0, base_height/2]) {
        rotate([0, 90, 0]) {
            // Handle grip
            color("darkgray") 
                cylinder(h=handle_length, d=handle_diameter, center=true);

            // Grip texture rings
            for(i = [-handle_length/2 + 5:5:handle_length/2 - 5]) {
                translate([i, 0, 0])
                    color("black") 
                        cylinder(h=0.5, d=handle_diameter + 1, center=true);
            }

            // End cap
            translate([-handle_length/2, 0, 0])
                color("red") 
                    sphere(d=handle_diameter + 2);

            // Transition to base
            translate([handle_length/2, 0, 0])
                color("silver") 
                    cylinder(h=10, d1=handle_diameter, d2=20, center=false);
        }
    }
}

module bubble_level() {
    translate([base_length/2 - 10, 0, base_height/2 + 5]) {
        // Level housing
        difference() {
            color("lightgray") 
                cube([20, 15, 10], center=true);
            translate([0, 0, -2])
                cylinder(h=12, d=8, center=true);
        }

        // Bubble tube
        translate([0, 0, 1])
            color("lightblue") 
                cylinder(h=6, d=6, center=true, $fn=20);

        // Center marks
        for(i = [-2, 2]) {
            translate([i, 0, 5.5])
                color("red") 
                    cube([0.5, 0.5, 1], center=true);
        }
    }
}

module adjustable_stops() {
    // Movable alignment stops with thumb screws
    module stop(x_pos) {
        translate([x_pos, 0, base_height/2 + 5]) {
            // Stop block
            difference() {
                color("orange") 
                    cube([6, base_width-4, 8], center=true);

                // Thumb screw hole
                translate([0, 0, -2])
                    cylinder(h=12, d=4, center=true);
            }

            // Thumb screw head
            translate([0, 0, 8])
                color("silver") 
                    cylinder(h=3, d=8, center=false);
        }
    }

    stop(15);
    stop(-15);
}

// Assembly
union() {
    base_with_guides();
    handle();
    bubble_level();
    adjustable_stops();

