// OpenSCAD code for a realistic handheld projector tool (50-100 mm size)
// Designed for 3D printing with no overhangs and 1.2 mm wall thickness

// Main dimensions (mm)
body_width = 40;
body_height = 30;
body_length = 80;
lens_diameter = 25;
lens_length = 15;
handle_diameter = 20;
handle_length = 60;
button_width = 8;
button_height = 4;
button_depth = 3;

// Colors
color_body = [0.2, 0.2, 0.2];  // Dark gray
color_lens = [0.1, 0.1, 0.8]; // Blue tint
color_handle = [0.1, 0.1, 0.1]; // Black
color_button = [0.8, 0.1, 0.1]; // Red
color_ring = [0.7, 0.7, 0.7]; // Silver

// Main projector body
module projector_body() {
    color(color_body)
    difference() {
        // Main rectangular body
        translate([0, 0, body_height/2])
            cube([body_length, body_width, body_height], center=true);

        // Cooling vents on top
        for (i = [1:3]) {
            translate([body_length/2 - 15 - i*8, 0, body_height - 2])
                cube([10, body_width - 10, 2]);
        }

        // Lens hole
        translate([body_length/2 + lens_length/2, 0, 0])
            rotate([0, 90, 0])
                cylinder(h=lens_length*2, d=lens_diameter + 2, center=true);
    }

    // Lens barrel
    color(color_lens)
    translate([body_length/2, 0, 0])
        cylinder(h=lens_length, d=lens_diameter);

    // Focus ring
    color(color_ring)
    difference() {
        translate([body_length/2 - 2, 0, 0])
            cylinder(h=6, d=lens_diameter + 8);
        translate([body_length/2 - 2, 0, 0])
            cylinder(h=7, d=lens_diameter + 2);
    }

    // Control buttons
    color(color_button)
    for (i = [0:1]) {
        translate([body_length/2 - 20 - i*12, body_width/2 + 2, body_height/2 - 5 - i*6])
            cube([button_width, button_depth, button_height]);
    }

    // LED indicator
    color([0, 1, 0])
    translate([body_length/2 - 10, body_width/2 + 1.5, body_height/2 - 15])
        sphere(d=3);
}

// Handle with grip texture
module handle() {
    color(color_handle)
    union() {
        // Main handle
        rotate([90, 0, 0])
            cylinder(h=handle_length, d=handle_diameter);

        // Grip rings
        for (i = [1:4]) {
            translate([0, 0, -handle_length/2 + i*10])
                cylinder(h=2, d=handle_diameter + 2);
        }

        // Connection to body
        translate([0, 0, handle_length/2])
            cube([body_width, handle_diameter, handle_diameter], center=true);
    }
}

// Tripod mount
module tripod_mount() {
    color(color_ring)
    difference() {
        translate([0, 0, -body_height/2 - 5])
            cylinder(h=10, d=15);
        translate([0, 0, -body_height/2 - 6])
            cylinder(h=12, d=8);
    }

    // Screw threads (simplified)
    for (i = [0:5]) {
        rotate([0, 0, i*30])
            translate([3.5, 0, -body_height/2 - 5])
                cube([1, 1, 10]);
    }
}

// Assembly
union() {
    projector_body();
    translate([0, 0, -body_height/2 - handle_length/2])
        handle();
    tripod_mount();
}

// Vernier Caliper Display Tool
// Realistic style, medium complexity

// Main dimensions (in mm)
$fn = 50;
overall_length = 90;
jaw_depth = 20;
jaw_thickness = 4;
slide_width = 15;
slide_thickness = 6;
scale_length = 70;

// Vernier caliper module
module vernier_caliper() {
    // Main beam
    color("silver") {
        // Beam body
        difference() {
            union() {
                // Main beam
                translate([-5, 0, 0])
                    cube([scale_length + 10, slide_width, slide_thickness]);

                // Fixed jaw
                translate([scale_length - 5, 0, 0])
                    cube([jaw_thickness, jaw_depth, jaw_thickness * 2]);
            }

            // Scale markings on beam
            for(i = [0:2:scale_length/2]) {
                translate([i * 2 - 5, slide_width - 0.5, slide_thickness - 0.5])
                    cube([0.5, 2, 0.5]);
            }
        }

        // Sliding jaw assembly
        translate([scale_length * 0.6, 0, 0]) {
            // Slide body
            difference() {
                cube([slide_width, slide_width + 5, slide_thickness + 2]);

                // Thumb wheel hole
                translate([slide_width/2, slide_width/2 + 2.5, -1])
                    cylinder(h = slide_thickness + 4, r = 3);
            }

            // Moving jaw
            translate([slide_width, 0, 0])
                cube([jaw_thickness, jaw_depth, jaw_thickness * 2]);

            // Thumb wheel
            color("gray") {
                translate([slide_width/2, slide_width/2 + 2.5, -1]) {
                    cylinder(h = slide_thickness + 4, r = 3.5);
                    for(a = [0:30:330]) {
                        rotate([90, 0, a])
                            translate([2.5, 0, 0])
                                cube([1, 0.5, 1]);
                    }
                }
            }

            // Locking screw
            color("black") {
                translate([slide_width/2, slide_width + 2, slide_thickness/2]) {
                    cylinder(h = 3, r = 2);
                    translate([0, 0, 3])
                        cylinder(h = 2, r1 = 2, r2 = 3);
                }
            }

            // Vernier scale on slide
            color("black") {
                translate([0.5, slide_width - 0.5, slide_thickness + 1])
                    cube([slide_width - 1, 1, 0.5]);
                for(i = [0:4]) {
                    translate([i + 1, slide_width - 0.5, slide_thickness + 1])
                        cube([0.2, 1, 0.5]);
                }
            }
        }
    }

    // Measurement jaws detail
    color("silver") {
        // Fixed jaw inner surface
        translate([scale_length - 5, jaw_depth/2, jaw_thickness]) {
            cube([jaw_thickness, 0.5, 2]);
        }

        // Moving jaw inner surface
        translate([scale_length * 0.6 + slide_width, jaw_depth/2, jaw_thickness]) {
            cube([jaw_thickness, 0.5, 2]);
        }

        // Depth rod
        translate([-5, slide_width/2, 0]) {
            rotate([90, 0, 0])
                cylinder(h = jaw_depth * 0.8, r = 1);
        }
    }
}

// Display base
module display_base() {
    color("darkslategray") {
        difference() {
            union() {
                // Main base
                cube([100, 40, 5]);

                // Raised platform
                translate([10, 10, 5])
                    cube([80, 20, 3]);
            }

            // Label recess
            translate([45, 12, 8])
                cube([10, 1, 0.5]);
        }

        // Corner fillets
        translate([5, 5, 0])
            cylinder(h = 5, r = 2);
        translate([95, 5, 0])
            cylinder(h = 5, r = 2);
        translate([5, 35, 0])
            cylinder(h = 5, r = 2);
        translate([95, 35, 0])
            cylinder(h = 5, r = 2);
    }
}

// Assembly
translate([0, 0, 10])
    rotate([0, -20, 15])
        vernier_caliper();

translate([0, 0, 0])
    display_base();

// OpenSCAD Interface Tool - Realistic Medium Complexity
// 3D Printable Network Cable Crimping Tool

$fn = 50;

// Overall dimensions
length = 90;
width = 50;
height = 15;

// Color definitions
handle_color = "DarkSlateGray";
metal_color = "Silver";
accent_color = "Red";

// Main module
module interface_tool() {
    // Left handle assembly
    translate([-length/2, 0, 0]) {
        color(handle_color) left_handle();
    }

    // Right handle assembly
    translate([length/2, 0, 0]) {
        rotate([0, 0, 180]) {
            color(handle_color) right_handle();
        }
    }

    // Pivot mechanism
    translate([0, 0, height/2]) {
        color(metal_color) pivot();
    }

    // Crimping heads
    translate([-length/3, 0, height]) {
        rotate([0, 0, 45]) {
            color(metal_color) crimp_head();
        }
    }

    translate([length/3, 0, height]) {
        rotate([0, 0, -45]) {
            color(metal_color) crimp_head();
        }
    }
}

// Left handle with grip
module left_handle() {
    difference() {
        // Main handle body
        cube([length/2 - 5, width, height], center = true);

        // Ergonomic grip cuts
        for (i = [1:5]) {
            translate([i * 8 - length/4, 0, height/2 - 2]) {
                cube([2, width - 8, 4], center = true);
            }
        }

        // Cable guide slot
        translate([length/4, 0, -height/2]) {
            cube([10, 6, height + 2], center = true);
        }
    }

    // Grip texture
    for (i = [0:4]) {
        translate([i * 8 - length/4 + 4, 0, -height/2 + 1]) {
            color(accent_color) {
                cube([1, width - 10, 0.5], center = true);
            }
        }
    }
}

// Right handle with grip
module right_handle() {
    left_handle();
}

// Pivot bolt assembly
module pivot() {
    union() {
        // Main pivot pin
        cylinder(h = 20, r = 5, center = true);

        // Bolt head
        translate([0, 0, 10]) {
            cylinder(h = 3, r = 8, center = true);
            for (i = [0:5]) {
                rotate([0, 0, i * 60]) {
                    translate([6, 0, 10]) {
                        cube([2, 1, 3], center = true);
                    }
                }
            }
        }
    }
}

// Crimping head with die
module crimp_head() {
    difference() {
        // Main head body
        cube([25, 18, 8], center = true);

        // RJ45 crimping die
        translate([0, 0, 4]) {
            cube([20, 12, 2], center = true);

            // Pin slots
            for (i = [-4:4]) {
                translate([i * 2, 0, 1]) {
                    cube([0.8, 10, 2], center = true);
                }
            }

            // Cable strain relief dies
            translate([0, -8, 0]) {
                cube([16, 2, 2], center = true);
                cube([14, 1.5, 1], center = true);
            }
        }
    }

    // Blade edge for cutting
    translate([0, 9, 4]) {
        cube([22, 0.5, 2], center = true);
    }
}

// Render the tool
interface_tool();

