    translate([0, 0, tip_length])
    color(color_body)
    difference() {
        cylinder(h = stylus_length - tip_length - 2, d = body_diameter);
        // Internal hollow for weight reduction
        translate([0, 0, -1])
        cylinder(h = stylus_length - tip_length, d = body_diameter - 3);
