// Blade length
blade_l = 45;
// Blade thickness (tapers toward edge)
blade_t = 3;

// Ferrule length
ferrule_l = 12;
// Ferrule outer diameter
ferrule_d = 10;

// Handle length
handle_l = 45;
// Handle diameter (tapers slightly)
handle_d1 = 14;
handle_d2 = 12;

// Grip ring geometry
ring_d = 16;
ring_t = 2;
ring_gap = 6;
ring_count = 3;

module blade() {
    // Main blade body
    difference() {
        // Tapered blade block
        hull() {
            translate([0, 0, blade_t/2])
                cube([blade_l, blade_w, 0.1], center=true);
            translate([blade_l/2 - 2, 0, 0])
                cube([4, blade_w, blade_t], center=true);
        }
        // Grinding bevels – upper
        translate([blade_l - 8, 0, blade_t/2 - 0.5])
            rotate([0, -25, 0])
            cube([blade_l, blade_w + 2, blade_t], center=true);
        // Lower bevel
        mirror([0, 0, 1])
            translate([blade_l - 8, 0, blade_t/2 - 0.5])
            rotate([0, -25, 0])
            cube([blade_l, blade_w + 2, blade_t], center=true);
    }
}

module ferrule() {
    color([0.7, 0.7, 0.7])
    difference() {
        cylinder(h = ferrule_l, d = ferrule_d, $fn = 32);
        translate([0, 0, -0.1])
            cylinder(h = ferrule_l + 0.2, d = ferrule_d - 2, $fn = 32);
    }
}

module handle() {
    color([0.4, 0.2, 0.1])
    union() {
        // Main tapered handle
        cylinder(h = handle_l, d1 = handle_d1, d2 = handle_d2, $fn = 32);
        // Grip rings
        for (i = [0 : ring_count - 1]) {
            translate([0, 0, handle_l - (i + 0.5) * ring_gap])
                cylinder(h = ring_t, d = ring_d, $fn = 32);
        }
        // Strike cap
        translate([0, 0, handle_l])
            cylinder(h = 3, d = handle_d1, $fn = 32);
    }
}

// Assemble the chisel
union() {
    translate([0, 0, -blade_t/2])
        blade();
    translate([-ferrule_l, 0, 0])
        ferrule();
    translate([-ferrule_l - handle_l, 0, 0])
        handle();
}

