                difference() {
                    circle(r = protractor_radius - 5);
                    square([protractor_radius * 2 - 10, protractor_radius - 5]);
                }
            }
    }
}

module degree_markings() {
    color(color_markings) {
        // Major marks every 10 degrees
        for (i = [0:10:180]) {
            rotate([0, 0, i - 90]) {
                translate([protractor_radius - 2, -0.5, protractor_thickness - marking_depth])
                    cube([2, 1, marking_depth]);
            }
        }

        // Minor marks every 5 degrees
        for (i = [5:10:175]) {
            rotate([0, 0, i - 90]) {
                translate([protractor_radius - 1.5, -0.25, protractor_thickness - marking_depth])
                    cube([1.5, 0.5, marking_depth]);
            }
        }

        // Degree numbers
        for (i = [0:30:180]) {
            rotate([0, 0, i - 90]) {
                translate([protractor_radius - 7, -2, protractor_thickness - marking_depth])
                    linear_extrude(height = marking_depth) {
                        text(str(i), size = 3, halign = "center", valign = "center");
                    }
            }
        }
    }
}

module ruler_markings() {
    color(color_markings) {
        // Millimeter marks
        for (i = [0:1:40]) {
            translate([-protractor_radius + i, -protractor_radius, protractor_thickness - marking_depth]) {
                if (i % 10 == 0) {
                    // Centimeter marks
                    cube([0.5, 5, marking_depth]);
                    translate([0, -8, 0])
                        linear_extrude(height = marking_depth)
                            text(str(i/10), size = 2.5, halign = "center");
                } else if (i % 5 == 0) {
                    // 5mm marks
                    cube([0.3, 3, marking_depth]);
                } else {
                    // 1mm marks
                    cube([0.2, 2, marking_depth]);
                }
            }
        }
    }
}

module handle() {
    color(color_handle) {
        // Main handle body
        translate([0, -protractor_radius - 25, -handle_thickness/2 + protractor_thickness/2])
            rotate([90, 0, 0])
                cylinder(h = handle_length, r1 = handle_width/2, r2 = handle_width/2 - 2);

        // Grip texture rings
        for (i = [10:10:50]) {
            translate([0, -protractor_radius - 25 - i, -handle_thickness/2 + protractor_thickness/2])
                rotate([90, 0, 0])
                    cylinder(h = 2, r = handle_width/2 + 0.5, center = true);
        }

        // Handle base connection
        translate([0, -protractor_radius - 10, -handle_thickness/2 + protractor_thickness/2])
            rotate([90, 0, 0])
                cylinder(h = 20, r1 = handle_width/2 + 2, r2 = handle_width/2);
    }
}

module center_mark() {
    color(color_markings) {
        // Center point indicator
        translate([0, 0, protractor_thickness - marking_depth])
            cylinder(h = marking_depth + 0.1, r = 1, $fn = 32);

        // Crosshair
        translate([0, 0, protractor_thickness - marking_depth])
            cube([0.5, 10, marking_depth + 0.1], center = true);
        translate([0, 0, protractor_thickness - marking_depth])
            rotate([0, 0, 90])
                cube([0.5, 10, marking_depth + 0.1], center = true);
    }
}

