    union() {
        // Central joint/hub
        translate([0, 0, compass_height/2])
        rotate([90, 0, 0])
        cylinder(h=joint_diameter*1.5, r=joint_diameter, center=true);

        // First leg with pencil holder
        translate([0, 0, compass_height/2])
        rotate([90, 0, 0])
        translate([joint_diameter/2, 0, 0])
        rotate([0, 0, -30])
        leg_assembly(leg_length, "pencil");

        // Second leg with needle
        translate([0, 0, compass_height/2])
        rotate([90, 0, 180])
        translate([joint_diameter/2, 0, 0])
        rotate([0, 0, 30])
        leg_assembly(leg_length, "needle");

        // Adjustment screw
        translate([joint_diameter*1.2, 0, compass_height/2])
        rotate([90, 0, 0])
        cylinder(h=8, r=4);

        // Locking mechanism
        translate([joint_diameter*1.2, 0, compass_height/2])
        rotate([90, 0, 0])
        cylinder(h=5, r=6);
    }
}

// Individual leg assembly
module leg_assembly(length, type) {
    difference() {
        union() {
            // Main leg
            color("silver")
            cylinder(h=length, r=3);

            // Handle grip
            translate([0, 0, length - handle_length])
            color("darkgray")
            cylinder(h=handle_length, r=4);

            // Grip texture rings
            for(i = [0:4]) {
                translate([0, 0, length - handle_length + i*5 + 2.5])
                color("gray")
                cylinder(h=2, r=4.2);
            }

            // End fitting
            if(type == "pencil") {
                // Pencil holder
                translate([0, 0, length])
                color("silver")
                cylinder(h=15, r1=4, r2=2);

                // Pencil
                translate([0, 0, length + 15])
                color("yellow")
                cylinder(h=10, r=pencil_radius);
            } else {
                // Needle holder
                translate([0, 0, length])
                color("silver")
                cone(8, 5, 1);

                // Sharp needle
                translate([0, 0, length + 8])
                color("darkgray")
                cylinder(h=needle_length, r=0.5);
            }
        }

        // Center hole for joint
        translate([0, 0, -joint_diameter/2])
        cylinder(h=joint_diameter*2, r=1.5);
    }
}

