// Main frame arc (60 degrees)
module frame_arc() {
    color(frame_color) {
        difference() {
            // Outer arc
            rotate([0, 0, -30])
                rotate_extrude(angle = 60)
                    translate([frame_radius, 0, 0])
                        square([frame_thickness, limb_width], center = true);

            // Inner cutout
            rotate([0, 0, -30])
                rotate_extrude(angle = 60)
                    translate([frame_radius - frame_thickness, 0, 0])
                        square([frame_thickness, limb_width + 2], center = true);
        }
    }
}

// Index arm
module index_arm() {
    color(frame_color) {
        // Main arm
        rotate([0, 0, 0])  // Will be positioned by caller
            linear_extrude(height = limb_width)
                polygon(points = [
                    [0, -2], [frame_radius - 5, -2], 
                    [frame_radius - 5, 2], [0, 2]
                ]);

        // Vernier scale detail
        for(i = [0:9]) {
            translate([frame_radius - 10 + i, -3, limb_width/2])
                cube([0.5, 3, 0.2]);
        }
    }
}

// Index mirror
module index_mirror() {
    color(mirror_color) {
        // Mirror holder
        translate([frame_radius - 15, 0, limb_width/2 + 2])
            rotate([0, 45, 0])
                cube([mirror_size, mirror_size, 1], center = true);

        // Actual mirror surface
        color(lens_color) {
            translate([frame_radius - 15, 0, limb_width/2 + 2.5])
                rotate([0, 45, 0])
                    cube([mirror_size - 2, mirror_size - 2, 0.2], center = true);
        }
    }
}

