    color(mirror_color) {
        // Mirror holder
        translate([frame_radius - 5, 0, limb_width/2 + 2])
            rotate([0, 45, 0])
                cube([mirror_size/2, mirror_size, 1], center = true);

        // Half-silvered mirror
        color(lens_color) {
            translate([frame_radius - 5, 0, limb_width/2 + 2.5])
                rotate([0, 45, 0])
                    cube([mirror_size/2 - 1, mirror_size - 2, 0.2], center = true);
        }
    }
}

// Telescope
module telescope() {
    color(frame_color) {
        translate([frame_radius - 5, -frame_radius/2, limb_width/2 + 5])
            rotate([90, 0, 0])
                cylinder(h = telescope_length, r1 = 3, r2 = 4);

        // Eyepiece
        translate([frame_radius - 5, -frame_radius/2 - telescope_length, limb_width/2 + 5])
            rotate([90, 0, 0])
                cylinder(h = 3, r = 4.5);
    }
}

// Handle
module handle() {
    color(frame_color) {
        // Main grip
        translate([0, 0, -handle_length/2])
            rotate([90, 0, 0])
                cylinder(h = handle_length, r = handle_diameter/2);

