// Goniometer Tool - Medium Complexity Realistic Style
// Medical protractor for measuring joint angles

$fn = 32;  // Smooth curves for 3D printing

// Main colors
color_body = [0.9, 0.9, 0.9];  // Light gray
color_markings = [0.2, 0.2, 0.2];  // Black
color_grip = [0.1, 0.1, 0.1];  // Dark gray
color_highlight = [0.8, 0.8, 0.8];  // Slightly lighter

// Overall dimensions
arm_length = 45;
arm_width = 7;
arm_thickness = 3;
hinge_diameter = 12;
hinge_height = 4;
grip_length = 15;

// Main body with arms
module goniometer() {
    difference() {
        union() {
            // First arm
            color(color_body)
            translate([0, -arm_length/2, 0])
            rotate([0, 0, 0])
            cube([arm_width, arm_length, arm_thickness], center=true);

            // Second arm (rotatable part)
            color(color_body)
            translate([0, -arm_length/2, 0])
            rotate([0, 0, 180])
            cube([arm_width, arm_length, arm_thickness], center=true);

            // Central hinge cylinder
            color(color_highlight)
            translate([0, 0, 0])
            cylinder(h=hinge_height, d=hinge_diameter, center=true);

            // Grip areas on arms
            color(color_grip)
            translate([0, -arm_length + grip_length/2, -arm_thickness/2 - 0.1])
            cube([arm_width + 1, grip_length, 1.5]);

            color(color_grip)
            translate([0, arm_length - grip_length/2, -arm_thickness/2 - 0.1])
            cube([arm_width + 1, grip_length, 1.5]);
        }

        // Center hole in hinge
        color(color_markings)
        translate([0, 0, -hinge_height/2 - 0.1])
        cylinder(h=hinge_height + 0.2, d=hinge_diameter/3, center=false);
    }
}

// Degree markings on arms
module markings() {
    for (i = [0:5:180]) {
        if (i % 15 == 0) {
            // Major tick marks
            color(color_markings)
            rotate([90, 0, 0])
            translate([0, -hinge_diameter/2 - 2, 0])
            rotate([0, 0, i])
            translate([0, 0, 0])
            cube([0.5, 3, 0.3], center=true);
        } else {
            // Minor tick marks
            color(color_markings)
            rotate([90, 0, 0])
            translate([0, -hinge_diameter/2 - 1.5, 0])
            rotate([0, 0, i])
            translate([0, 0, 0])
            cube([0.3, 1.5, 0.2], center=true);
        }
    }

    // Numbers every 15 degrees
    for (i = [0:15:180]) {
        if (i <= 180) {
            color(color_markings)
            rotate([90, 0, 0])
            translate([0, -hinge_diameter/2 - 4.5, 0])
            rotate([0, 0, i])
            translate([0, 0, 0])
            linear_extrude(height=0.5)
            text(str(i), size=2, halign="center", valign="center");
        }
    }
}

// Arm extensions for better measurement
module extensions() {
    // Left extension
    color(color_body)
    translate([0, -arm_length - 5, 0])
    cube([1.5, 10, arm_thickness], center=true);

    // Right extension
    color(color_body)
    translate([0, arm_length + 5, 0])
    cube([1.5, 10, arm_thickness], center=true);
}

// Assembly
translate([0, 0, 0]) {
    goniometer();
    markings();
    extensions();
}

