color_display = [0.1, 0.9, 0.1];
color_buttons = [0.7, 0.7, 0.7];

// Main body of chronoscope
module chronoscope_body() {
    color(color_body)
    union() {
        // Main housing
        translate([-body_length/2, 0, 0])
            cube([body_length, body_width, body_thickness], center=true);

        // Rounded edges
        translate([-body_length/2 + body_thickness/2, 0, 0])
            cylinder(h=body_width, r=body_thickness/2, center=true);
        translate([body_length/2 - body_thickness/2, 0, 0])
            cylinder(h=body_width, r=body_thickness/2, center=true);
    }
}

// Handle with grip texture
module handle() {
    color(color_handle)
    difference() {
        // Main handle cylinder
        translate([body_length/2 + handle_length/2, 0, 0])
            rotate([0, 90, 0])
                cylinder(h=handle_length, r=handle_diameter/2, center=true);

        // Grip grooves
        for(i = [1:3]) {
            translate([body_length/2 + handle_length/2, 0, handle_diameter/2 - i*3])
                rotate([0, 90, 0])
                    cylinder(h=handle_length, r=handle_diameter/2 - 1, center=true);
        }
    }
}

// Analog dial face
module analog_dial() {
    color(color_dial)
    difference() {
        // Dial base
        translate([-body_length/3, body_width/4, body_thickness/2 + 0.5])
            cylinder(h=2, r=12, center=false);

        // Dial center hole
        translate([-body_length/3, body_width/4, body_thickness/2 + 2.5])
            cylinder(h=3, r=2, center=false);
    }

    // Hour markers
    for(i = [0:11]) {
        angle = i * 30;
        color([0.1, 0.1, 0.1])
            translate([-body_length/3, body_width/4, body_thickness/2 + 2.5])
                rotate([0, 0, angle])
                    translate([10, 0, 0])
                        cube([2, 1, 0.5], center=true);
    }

    // Minute hand
    color(color_needle)
    translate([-body_length/3, body_width/4, body_thickness/2 + 2.5])
        rotate([0, 0, 45])
            cube([8, 1, 0.5], center=true);

    // Hour hand
    color(color_needle)
    translate([-body_length/3, body_width/4, body_thickness/2 + 3])
        rotate([0, 0, 15])
            cube([6, 1.5, 0.5], center=true);
}

// Digital display
