    color([0.05, 0.05, 0.05])
    translate([body_length/6, -body_width/4, body_thickness/2 + 0.5])
        cube([25, 12, 1], center=false);

    color(color_display)
    translate([body_length/6 + 1, -body_width/4 + 1, body_thickness/2 + 1.5])
        cube([23, 10, 0.5], center=false);

    // Display segments (simplified 7-segment)
    for(i = [0:3]) {
        color([0.8, 0.9, 0.8])
        translate([body_length/6 + 3 + i*5, -body_width/4 + 2, body_thickness/2 + 2])
            cube([3, 6, 0.3], center=false);
    }
}

