    color(color_buttons)
    translate([-body_length/4, body_width/2 + 3, 0])
        cylinder(h=3, r=4, center=true);

    // Reset button
    color(color_buttons)
    translate([0, body_width/2 + 3, 0])
        cylinder(h=3, r=3, center=true);

    // Mode button
    color(color_buttons)
    translate([body_length/4, body_width/2 + 3, 0])
