        color([0.2, 0.2, 0.2])
        translate([0, 10, body_d/2+0.1])
        cube([screen_w+4, screen_h+4, screen_d], center=true);

        // Screen opening
        translate([0, 10, body_d/2+0.2])
        color([0.1, 0.1, 0.1])
        cube([screen_w, screen_h, screen_d+0.1], center=true);

        // LCD display
        translate([0, 10, body_d/2+0.3])
        color([0.3, 0.8, 0.3])
        cube([screen_w-2, screen_h-2, screen_d+0.2], center=true);
