module rangefinder_body() {
    // Main body - slightly tapered
    difference() {
        hull() {
            translate([0, 0, height/2])
                cube([length*0.9, width*0.85, height], center=true);
            translate([0, 0, height*0.4])
                cube([length, width, height*0.8], center=true);
        }

        // Front lens opening
        translate([-length/2 + 2, 0, 0])
            rotate([0, 90, 0])
                cylinder(h=width*0.6, r=width*0.2, center=true);

        // Screen opening
        translate([length/4, 0, height/2 - 1])
            cube([length*0.3, width*0.6, 2], center=true);
    }
}

module grip_texture() {
    // Rubber grip pattern on sides
    for (i = [-1, 1]) {
        translate([0, i*width*0.4, 0])
            for (j = [-3:3]) {
