    for (i = [0:2]) {
        translate([length/4 - 10, width/4 - i*8, height/2 + 1])
            cylinder(h=3, r=4, center=true);
    }

    // Power button
    translate([-length/3, width/3, height/2 + 1])
        cylinder(h=2, r=5, center=true);
}

module lens_assembly() {
    // Front lens system
    translate([-length/2, 0, 0])
        rotate([0, 90, 0]) {
            // Outer lens barrel
            cylinder(h=width*0.4, r=width*0.25, center=true);

            // Inner lens
            color(color_lens)
                cylinder(h=width*0.2, r=width*0.22, center=true);

            // Lens protector ring
            translate([width*0.2, 0, 0])
                cylinder(h=width*0.1, r=width*0.28, center=true);
        }
}

module display_screen() {
