    translate([length/4, 0, height/2])
        cube([length*0.28, width*0.55, 0.5], center=true);

    // Screen border
    translate([length/4, 0, height/2 - 0.5])
        cube([length*0.32, width*0.58, 1], center=true);
}

module viewfinder() {
    // Top viewfinder eyepiece
    translate([length/4, 0, height/2 + 4])
        rotate([90, 0, 0])
            cylinder(h=width*0.3, r=width*0.15, center=true);

