color_markings = [1, 1, 1];

// Main assembly
union() {
    // Base plate with rounded edges
    translate([0, 0, base_thickness/2]) {
        color(color_base) {
            difference() {
                minkowski() {
                    cube([body_width + 8, body_depth + 8, base_thickness], center=true);
                    sphere(2);
                }
                translate([0, 0, -1]) {
                    cube([body_width + 4, body_depth + 4, base_thickness + 2], center=true);
                }
            }
        }
    }

    // Main body
    translate([0, 0, base_thickness + body_height/2]) {
        color(color_body) {
            difference() {
                cube([body_width, body_depth, body_height], center=true);
                // Display cutout
                translate([0, body_depth/2 - 5, 0]) {
                    cube([display_width + 4, display_thickness + 2, display_height + 4], center=true);
                }
                // Battery compartment
                translate([-body_width/2 + 8, 0, -body_height/2 + 5]) {
                    cube([16, 25, 8], center=true);
                }
            }
        }
    }

    // Display screen
    translate([0, body_depth/2 - display_thickness/2 - 4, base_thickness + body_height/2]) {
        color(color_display) {
            cube([display_width, display_thickness, display_height], center=true);
        }
        // Display markings
        for(i = [-1:1]) {
            translate([i * 8, display_thickness/2 + 0.1, 0]) {
                color(color_markings) {
                    cube([1, 0.2, 8]);
                }
            }
        }
    }

    // Control buttons
    for(i = [0:2]) {
        translate([body_width/2 - 8, -body_depth/2 + 10 + i*12, base_thickness + body_height/2]) {
            color(color_base) {
                cylinder(h=4, r=3, center=true);
            }
        }
    }

    // Grip texture on sides
    for(i = [-1, 1]) {
        for(j = [0:4]) {
            translate([i * body_width/2, -body_depth/2 + 10 + j*10, base_thickness + body_height/2 + 5]) {
                color(color_grip) {
                    cube([2, 8, 2], center=true);
                }
            }
        }
    }

    // Probe shaft
    translate([0, -body_depth/2 - probe_length/2 + 10, base_thickness + body_height/2 - 5]) {
        color(color_probe) {
            cylinder(h=probe_length, r=probe_diameter/2, center=true);
        }

        // Measurement markings on probe
        for(i = [0:9]) {
            translate([0, -body_depth/2 + 10 + i*10, base_thickness + body_height/2 - probe_diameter/2 - 2]) {
                color(color_markings) {
                    cube([probe_diameter + 2, 0.5, 1], center=true);
                }
            }
            translate([probe_diameter/2 + 3, -body_depth/2 + 10 + i*10, base_thickness + body_height/2 - 5]) {
                color(color_markings) {
                    rotate([90, 0, 0]) {
                        linear_extrude(height=0.5) {
                            text(str(i*10), size=3, valign="center", halign="center");
                        }
                    }
                }
            }
        }
    }

    // Probe tip
    translate([0, -body_depth/2 - probe_length + 10, base_thickness + body_height/2 - 5]) {
        color(color_probe) {
            cylinder(h=8, r1=probe_diameter/2, r2=0, center=true);
        }
    }

    // Reference block
    translate([0, -body_depth/2 - probe_length - 15, base_thickness + body_height/2 - 5]) {
        color(color_base) {
            cube([20, 10, 6], center=true);
            translate([0, 0, -3]) {
                cylinder(h=6, r=10, center=true);
            }
        }
    }
}

