capillary_diameter = 1.5;

module timing_mark() {
    color(color_markings) 
    translate([0, -tube_diameter/2-1, 0])
    cube([0.5, 0.5, 0.2], center=true);
}

module timing_marks(z_pos) {
    for (i = [0:1]) {
        translate([0, 0, z_pos + i*capillary_length/2])
        timing_mark();
    }
}

module reservoir() {
    color(color_reservoir)
    union() {
        // Main reservoir bulb
        cylinder(d=reservoir_diameter, h=reservoir_height, center=true);

        // Connection to tube
        translate([0, 0, reservoir_height/2])
        cylinder(d=tube_diameter, h=2, center=false);
    }
}

module measuring_bulb() {
    color(color_tube)
    translate([0, 0, -tube_length/2 + capillary_length + bulb_volume])
    sphere(d=bulb_diameter, center=true);
}

module capillary_tube() {
    color(color_tube)
    union() {
        // Lower tube
        translate([0, 0, -tube_length/2 + capillary_length/2])
        cylinder(d=tube_diameter, h=capillary_length, center=true);

        // Upper tube
        translate([0, 0, tube_length/2 - (tube_length - capillary_length - bulb_volume)/2])
        cylinder(d=tube_diameter, h=(tube_length - capillary_length - bulb_volume), center=true);

        // Capillary section
        translate([0, 0, -tube_length/2 + capillary_length/4])
        cylinder(d=capillary_diameter, h=capillary_length/2, center=true);
    }
}

module support_stand() {
    color(color_stand)
    union() {
        // Base
        translate([0, -15, -tube_length/2-5])
        cube([30, 5, 2], center=true);

        // Upright supports
        for (x = [-12, 12]) {
            translate([x, -15, -tube_length/2-2.5])
            cube([2, 5, tube_length+8], center=true);
        }

        // Top support
        translate([0, -15, tube_length/2+3])
        cube([30, 5, 2], center=true);

        // Clamp rings
        for (z = [-tube_length/2+5, tube_length/2-5]) {
            translate([0, -12, z])
            rotate([90, 0, 0])
            cylinder(d=tube_diameter+2, h=3, center=true);
        }
    }
}

// Assembly
union() {
    // Main viscometer tube
    translate([0, 0, 0])
    union() {
        reservoir();
        translate([0, 0, reservoir_height/2 + tube_length/2 - capillary_length/2])
        capillary_tube();
        measuring_bulb();
        timing_marks(-tube_length/2 + capillary_length/4);
    }

    // Support stand
    support_stand();
}

