// U-tube manometer with pressure fittings, scale markings, and base

// Main parameters
base_width = 40;
base_depth = 20;
base_height = 8;
tube_diameter = 6;
tube_wall = 1;
tube_height = 50;
manometer_width = 30;
scale_length = 40;

// Colors
base_color = [0.2, 0.2, 0.2];
tube_color = [0.8, 0.8, 0.9, 0.7];
fluid_color = [0.1, 0.6, 0.9, 0.8];
metal_color = [0.7, 0.7, 0.7];

// Base with mounting holes
module manometer_base() {
    color(base_color)
    difference() {
        // Main base
        cube([base_width, base_depth, base_height], center=true);

        // Mounting holes
        translate([15, 0, -base_height/2])
            cylinder(h=base_height+1, d=3, center=true);
        translate([-15, 0, -base_height/2])
            cylinder(h=base_height+1, d=3, center=true);

        // Label recess
        translate([0, 8, base_height/2-1])
            cube([20, 8, 2], center=true);
    }
}

// U-tube assembly
module u_tube() {
    color(tube_color)
    union() {
        // Left vertical tube
        translate([-manometer_width/2, 0, tube_height/2])
            cylinder(h=tube_height, d=tube_diameter+tube_wall*2, center=true);

        // Right vertical tube
        translate([manometer_width/2, 0, tube_height/2])
            cylinder(h=tube_height, d=tube_diameter+tube_wall*2, center=true);

        // Bottom connecting tube
        translate([0, 0, tube_wall])
            rotate([90, 0, 0])
            cylinder(h=manometer_width+tube_diameter, d=tube_diameter+tube_wall*2, center=true);

        // Left pressure fitting
        color(metal_color)
        translate([-manometer_width/2, 0, tube_height+3])
            cylinder(h=6, d=8, center=true);

        // Right pressure fitting
        translate([manometer_width/2, 0, tube_height+3])
            cylinder(h=6, d=8, center=true);
    }

    // Fluid inside tube
    color(fluid_color)
    difference() {
        // Left fluid column
        translate([-manometer_width/2, 0, tube_height/4])
            cylinder(h=tube_height/2, d=tube_diameter, center=true);

        // Right fluid column (higher due to pressure)
        translate([manometer_width/2, 0, tube_height/3])
            cylinder(h=tube_height/1.5, d=tube_diameter, center=true);

        // Bottom fluid connection
        translate([0, 0, tube_wall])
            rotate([90, 0, 0])
            cylinder(h=manometer_width, d=tube_diameter, center=true);
    }
}

// Scale markings
module scale_markings() {
    color([0.1, 0.1, 0.1])
    for(i=[-20:1:20]) {
        height = i * 1 + tube_height/2;
        if(i%5 == 0) {
            // Major tick marks
            translate([manometer_width/2+5, 0, height])
                cube([3, 1, 0.5], center=true);
            translate([-manometer_width/2-5, 0, height])
                cube([3, 1, 0.5], center=true);
        } else {
            // Minor tick marks
            translate([manometer_width/2+4, 0, height])
                cube([2, 0.5, 0.3], center=true);
            translate([-manometer_width/2-4, 0, height])
                cube([2, 0.5, 0.3], center=true);
        }
    }
}

// Support structure
module support_structure() {
    color(metal_color)
    union() {
        // Left support
        translate([-manometer_width/2-2, 0, tube_height/2])
            cylinder(h=tube_height, d=4, center=true);

