// Telescoping tool with extendable handle and interchangeable bits
// Overall length: 60mm retracted, 100mm extended
// Features: threaded sections, knurled grip, keyring loop, magnetic bit holder

$fn = 50;
wall = 1.5;
bit_d = 6;
handle_d = 12;

module knurl(diameter, height, knurl_w=1.2, knurl_h=0.5) {
    difference() {
        cylinder(d=diameter, h=height, center=true);
        for(i=[0:30:359]) {
            rotate([0,0,i])
                translate([diameter/2, 0, 0])
                    cube([knurl_w, diameter, height+1], center=true);
            rotate([90,0,i])
                translate([diameter/2, 0, 0])
                    cube([knurl_w, diameter, height+1], center=true);
        }
    }
    color("silver") cylinder(d=diameter, h=height, center=true);
}

module screw_thread(outer_d, inner_d, height, pitch=2) {
    slices = floor(height/pitch);
    for(i=[0:slices-1]) {
        translate([0,0,i*pitch-pitch/2])
            color("silver") cylinder(d1=outer_d, d2=inner_d, h=pitch, center=true);
    }
}

module bit_holder() {
    color("darkgray") {
        difference() {
            cylinder(d=bit_d+2, h=8, center=true);
            cylinder(d=bit_d+0.5, h=9, center=true);
        }
        cylinder(d=bit_d+4, h=2, center=true);
    }

    // Magnet cavity
    color("red") translate([0,0,-3.5])
        cylinder(d=bit_d-1, h=3, center=true);
}

module handle_main() {
    color("black") {
        // Main tube
        difference() {
            cylinder(d=handle_d, h=40, center=true);
            cylinder(d=handle_d-wall*2, h=42, center=true);
        }

        // Grip section
        translate([0,0,-15])
            knurl(handle_d, 10);

        // Keyring loop
        translate([0,handle_d/2,15])
            rotate([90,0,0])
                difference() {
                    torus(handle_d/4, 2);
                    translate([-handle_d/2-1,0,0])
                        cube([handle_d+2, handle_d+2, 4], center=true);
                }
    }
}

module inner_tube() {
    color("darkgray") {
        difference() {
            cylinder(d=handle_d-4, h=45, center=true);
            cylinder(d=handle_d-4-wall*2, h=47, center=true);
        }

        // Threaded end
        translate([0,0,20])
            screw_thread(handle_d-4, handle_d-6, 5);
    }
}

module outer_tube() {
    color("silver") {
        difference() {
            cylinder(d=handle_d-8, h=50, center=true);
            cylinder(d=handle_d-8-wall*2, h=52, center=true);
        }

        // Threaded end
        translate([0,0,23])
            screw_thread(handle_d-8, handle_d-10, 5);
    }
}

module phillips_bit() {
    color("gold") {
        union() {
            cylinder(d=bit_d, h=20, center=true);
            translate([0,0,10.5]) {
                cube([bit_d, 1, 2], center=true);
                cube([1, bit_d, 2], center=true);
            }
        }
    }
}

// Assembly
difference() {
    union() {
        // Main handle
        handle_main();

        // Telescoping tubes
        translate([0,0,40])
            inner_tube();

        translate([0,0,85])
            outer_tube();

        // Bit holder on end
        translate([0,0,110])
            bit_holder();
    }

    // Remove internal voids
    translate([0,0,20])
        cylinder(d=handle_d-wall*2, h=200, center=true);
}

