color_labels = [0.9, 0.1, 0.1];

// Base with protractor markings
module base() {
    color(color_base) {
        // Main base cylinder
        cylinder(h = base_height, d = base_diameter);

        // Protractor degree markings
        for (i = [0:5:359]) {
            rotate([0, 0, i])
            translate([base_diameter/2 - 4, 0, base_height - 0.5])
            cube([3, 0.5, 1]);
        }

        // Main degree numbers (every 30 degrees)
        for (i = [0:30:330]) {
            rotate([0, 0, i])
            translate([base_diameter/2 - 8, 0, base_height - 0.5])
            color(color_labels)
            cube([1, 0.5, 0.8]);
        }

        // Center pivot
        translate([0, 0, base_height])
        cylinder(h = 4, d = 8);
    }
}

// Rotating arm assembly
module rotating_arm() {
    color(color_arm) {
        // Main arm
        translate([0, 0, base_height + 4])
        rotate([90, 0, 0])
        cylinder(h = arm_length, d = arm_thickness);

        // Arm support structure
        translate([0, 0, base_height + 4])
        rotate([90, 0, 0])
        cylinder(h = 8, d1 = arm_thickness, d2 = 10);

        // Detector housing at end of arm
        translate([0, 0, base_height + 4 + arm_length - detector_housing_size/2])
        cube([detector_housing_size, detector_housing_size, detector_housing_size], center = true);

        // Detector lens
        translate([0, 0, base_height + 4 + arm_length - detector_housing_size/2 - 1])
        color(color_detector)
        sphere(d = detector_housing_size * 0.6);
    }
}

// Light source mount
module light_source_mount() {
    color(color_arm) {
        // Vertical post
        translate([0, -base_diameter/2 + 10, 0])
        cylinder(h = light_source_height, d = 6);

        // Light source holder
        translate([0, -base_diameter/2 + 10, light_source_height])
        rotate([90, 0, 0])
        cylinder(h = 12, d = 8);

        // Light source (LED)
        translate([0, -base_diameter/2 + 10 + 6, light_source_height])
        color(color_light)
        sphere(d = light_source_diameter);
    }
}

// Stabilizing feet
module feet() {
    color(color_base) {
        // Three feet for stability
        for (angle = [0:120:240]) {
            rotate([0, 0, angle])
            translate([base_diameter/2 - 5, 0, -2])
            cylinder(h = 4, d = 6);
        }
    }
}

