module base() {
    // Main platform with mounting holes
    difference() {
        cube([80, 60, 5], center=true);

        // Mounting holes
        for(x = [-30, 30])
            for(y = [-20, 20])
                translate([x, y, -2.5])
                    cylinder(h=10, r=2, center=true);
    }

    // Support posts
    for(pos = [[-25, 0], [25, 0], [0, 25], [0, -25]]) {
        translate([pos[0], pos[1], 2.5])
            cylinder(h=15, r=3);
    }
}

module laser_source() {
    translate([-35, 0, 20]) {
        // Laser housing
        rotate([0, 90, 0])
            cylinder(h=15, r=4);

        // Mounting bracket
        translate([0, 0, -7.5])
            cube([8, 8, 3], center=true);
    }
}

module beam_splitter() {
    translate([0, 0, 20]) {
        rotate([0, 0, 45])
            cube([10, 10, 2], center=true);

        // Support block
        translate([0, 0, -6])
            cube([12, 12, 10], center=true);
    }
}

module fixed_mirror() {
    translate([25, 0, 20]) {
        // Mirror
        rotate([0, 0, 0])
            cube([12, 12, 2], center=true);

        // Adjustment screws
        for(x = [-5, 5])
            for(y = [-5, 5]) {
                translate([x, y, 6])
                    cylinder(h=3, r=1);
            }

        // Mount
        translate([0, 0, -5])
            cube([15, 15, 8], center=true);
    }
}

module movable_mirror() {
    translate([-25, 0, 20]) {
        // Mirror
        translate([0, 0, 5])
            cube([12, 12, 2], center=true);

        // Micrometer adjustment
        translate([-8, 0, 5])
            rotate([0, 90, 0])
                cylinder(h=20, r=3);

        // Micrometer knob
        translate([-18, 0, 5])
            cylinder(h=5, r=6);

        // Fine threads
